[[include component:translation-bars name=%%name%%]]
[[include component:article-bars]]
[[set system-page-author-override %%this|created_by%%]]
[[set system-page-author-en-override %%this|fullname%%]]

%%content%%

[[include component:article-achievements]]

[[iftags +ru]]
[[module CSS]]
#page-content {
  position: relative;
  padding-bottom: 82px;
}
[[/module]]
[[/iftags]]

[[module CSS]]
.more-author-articles {
  position: absolute;
  left: 0;
  bottom: 0;
  padding: 4px;
  padding-bottom: 0;
  border-top: 1px solid #BBB;
  clear: both;
}
.more-author-articles p {
  margin: 0; padding: 0;
}
[[/module]]

[[iftags +ru]]
[[div_ class="more-author-articles"]]
[[a href="/system:author-pages-ru/created_by/{@system-page-author-override}/order/rating desc"]]Другие статьи этого автора[[/a]]
[[/div]]
[[/iftags]]

[[iftags +en]]
[[div_ class="more-author-articles"]]
[[a href="/system:author-pages/fullname/{@system-page-author-en-override}"]]Другие статьи этого автора[[/a]]
[[/div]]
[[/iftags]]
