    --five-color: 196,2,51;
    --four-color: 255,109,0;
    --three-color: 255,211,0;
    --two-color: 0,135,189;
    --one-color: 0,159,107;
}

#footnoteref-6 {
   color: white !important;
}

#breadcrumbs,
#page-title {
    text-align: center;
}


#page-content .collapsible-block-folded a.collapsible-block-link {
