}

.desktop-display iframe,
.mobile-display iframe {
    width: -webkit-calc(100% + -webkit-calc(100vw - 100%));
    width: -moz-calc(100% + -moz-calc(100vw - 100%));
    width: calc(100% + calc(100vw - 100%));
