[[module CSS]]
.kig {
  display: flex;
  flex-wrap: wrap;
  align-items: stretch;
  justify-content: stretch;
  margin: -8px;
}
.kig .scp-image-block {
  float: none;
  clear: none;
  width: auto !important;
  flex-grow: 1;
  margin: 8px;
}
.kig img {
  height: 300px;
  min-width: 48px;
  object-fit: cover;
  width: 100% !important;
}
.kig .scp-image-caption {
  width: auto !important;
}
[[/module]]

