[[module CSS]]
/* HEADER */
 :root{
   --header-title: "Птичья команда";
   --header-subtitle: "Пока мы не вернемся в Гнездо";
   --lgurl: url(https://scpfoundation.net/local--files/bird/avdiv_white.png);
}

 div#container-wrap{
     background-image: url(https://scpfoundation.net/local--files/bird/avdiv_body_bg.png);
}

 #header h1 a::before,
 #header h2::before{
     font-family: "Verdana", sans-serif;
}

 #page-title,
 #breadcrumbs{
     display: none;
}

 .meta-title{
     color: hsl(45, 98%, 28%);
}

 #page-content a,
 #page-content span{
     font-weight: bold;
}

/* INFO BAR */
 body{
     --barColour: hsl(45, 89%, 40%);
}

 .info-container .collapsible-block-unfolded{
     background: hsl(0, 0%, 100%);
}

 #u-author_block{
     display: none;
}

/* PAGE RATING */
 .page-rate-widget-box .rate-points{
     background: hsl(45, 89%, 40%) !important;
     border: 1px solid hsl(45, 89%, 40%);
}

 .page-rate-widget-box .rateup,
 .page-rate-widget-box .ratedown{
     background: hsl(45, 100%, 97%);
     border-color: hsl(45, 89%, 40%);
}

 .page-rate-widget-box .cancel{
     background: hsl(45, 89%, 40%);
     border: 1px solid hsl(45, 89%, 40%);
}

 .page-rate-widget-box .rateup a,
 .page-rate-widget-box .ratedown a{
     color: hsl(45, 89%, 40%);
}

 .page-rate-widget-box .cancel a{
     color: hsl(45, 76%, 67%);
}

 .page-rate-widget-box .rateup a:hover,
 .page-rate-widget-box .ratedown a:hover{
     background: hsl(45, 89%, 40%);
}

 .page-rate-widget-box .cancel a:hover{
     color: hsl(45, 100%, 97%);
     background: hsl(45, 89%, 40%);
}

/* DIV BLOCKS */
 .header-image{
     margin-bottom: .25rem;

     background: hsl(0, 0%, 100%);
     border: 2px solid hsl(0, 0%, 5%);

     font-size: 80%;
     font-weight: bold;
     text-align: center;
}

 .showcase{
     padding: 0 1rem;

     background: hsl(0, 0%, 100%);
     border: 2px solid hsl(0, 0%, 5%);
}

 .showcase hr{
     border-top: 2px solid hsl(45, 100%, 55%);
}

 .scm{
    margin-bottom: 4%;
}

/* FORMATTING */
 .bg-gradiant{
     margin: 0 -1.7em;
     padding: 0 1.7em;

     background:
        linear-gradient(to bottom,
          transparent,
          hsl(45, 100%, 55%),
          hsl(45, 100%, 55%),
          hsl(0, 0%, 100%));
}

/* TABLES */
 blockquote,
 .blockquote,
 .article blockquote,
 .continued blockquote{
     background: 0;
     border: 1px solid hsl(45, 100%, 55%);
}

 blockquote,
 .blockquote{
     border-width: 2px;
}

 .article blockquote,
 .continued blockquote{
     padding-right: 0;
     padding-left: 0;

     border-left-width: 2px;
}

 .article blockquote{
     border-top-width: 2px;
}

 .article table.wiki-content-table,
 .continued table.wiki-content-table{
     width: 100%;
     margin: 0 0 -1px;
}

 .article table.wiki-content-table td,
 .continued table.wiki-content-table td{
     border-color: hsl(45, 100%, 55%);
     border-right: 0;
}

 .continued table.wiki-content-table td{
     border-top: 0;
}

/* FAKE AUTHOR BLOCK */
 .fakeblock a{
     display: inline;
     padding: .8em;

     color: hsl(0, 0%, 5%);
     background: hsl(45, 100%, 92%);
     border: .15rem outset hsl(45, 79%, 50%);
     transition:
        -webkit-backdrop-filter .3s,
        backdrop-filter .3s,
        background .3s;
}

 .fakeblock a:hover{
     background: var(--barColour);

     text-decoration: none;
}
[[/module]]

