[[module CSS]]
.shop-sign {
  background-color: #009999;
  overflow: auto;
  border: 7px solid #C0C0C0;
  border-radius: 75px;
  padding: 15px 0 0 0;
}

.shop-sign span {
  font-family: 'Cormorant SC', serif;
  color: white;
}

.shop-sign span.title {
  font-size: 180%;
}

.shop-sign span.subtitle {
  font-size: 130%;
}

@media (max-width: 900px) {
  .shop-sign span.title {
    font-size: 130%;
  }

  .shop-sign span.subtitle {
    font-size: 100%;
  }
}
[[/module]]

[[module CSS]]
.chancellery_doc {
  background: url(https://files.scpfoundation.net/local--files/case-of-the-mysterious-murder-of-the-fire-chief/yellow_paper_texture.jpg) center repeat;
  padding:  2px 20px;
  background-color: #FFFFFF;
}

.chancellery_doc span.title {
  font-size: 150%;
}

.chancellery_doc span.subtitle {
  font-size: 120%;
}

.chancellery_doc span.text {
  font-family: 'Great Vibes', serif;
  line-height: 120%;
  font-size: 225%;
}
[[/module]]

[[module CSS]]
.police_doc {
  background: url(https://files.scpfoundation.net/local--files/case-of-the-mysterious-murder-of-the-fire-chief/white_paper_texture.jpg) center repeat;
  background-color: #FFFFFF;
  overflow: hidden;
}

.police_doc span.left {
  width: 35%;
  float:left;
  padding: 5px;
}

.police_doc span.right {
  width: 60%;
  float:right;
  padding-right: 5px;
}

.police_doc span.title {
  font-family: 'Train One', serif;
  font-size: 280%;
}

.police_doc span.subtitle {
  font-size: 135%;
}

.police_doc span.number {
  font-family: 'Great Vibes', serif;
  line-height: 120%;
  font-size: 200%;
}

.police_doc span.text {
  font-family: 'Great Vibes', serif;
  line-height: 120%;
  font-size: 225%;
}

@media (max-width: 800px) {
  .police_doc span.title {
    font-size: 150%;
  }

  .police_doc span.subtitle {
    font-size: 100%;
  }

  .police_doc span.text {
    font-size: 175%;
  }
}
[[/module]]


