[[module CSS]]
/* Mobile compatible tooltips, stolen from the internet by John_Lyetacker, 2023 (c) */

/* Tooltip container */
.tooltip {
  position: relative;
  display: inline-block;
}

/* Tooltip text */
.tooltip .tooltiptext {
  visibility: hidden;
  width: auto;
  background-color: #252525;
  color: white;
  text-align: center;
  padding: 5px;
  border: 1px solid white;
  border-radius: 5px;
  position: absolute; /* Positioning tooltip text */
  z-index: 1;
  top: 80%;
  right: 5%;
  opacity: 0; /* Anime */
  transition: opacity 0.25s;
}

/* Tooltip link coloring */
.tooltip .tooltiptext a {
  color: white;
}

/* Show the tooltip text when you mouse over the tooltip container */
.tooltip:hover .tooltiptext {
  visibility: visible;
  opacity: 1;
}

/* Achievements-related CSS */
.achievements {
  display: flex;
  position: absolute;
  bottom: 32px; right: 0;
}

.achievement-icon {
  width: 50px;
  height: 50px;
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
}
[[/module]]

