[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Oswald:500&display=swap&subset=cyrillic');

div.scale {
    /* Classification Colors */
    --woedbar-keter-color: rgb(180, 30, 35);
    --woedbar-euclid-color: rgb(225, 205, 35);
    --woedbar-safe-color: rgb(35, 145, 70);
    --woedbar-thaumiel-color: rgb(40, 60, 150);
    --woedbar-neutral-color: rgb(127, 127, 127);
    /* Angled Bar Colors */
    --woedbar-lvl1-color: rgba(var(--swatch-menubg-black-color,12, 12, 12),0.1);
    --woedbar-lvl2-color: rgba(var(--swatch-menubg-black-color,12, 12, 12),0.3);
    --woedbar-lvl3-color: rgba(var(--swatch-menubg-black-color,12, 12, 12),0.55);
    --woedbar-lvl4-color: rgba(var(--swatch-menubg-black-color,12, 12, 12),0.75);
    --woedbar-lvl5-color: rgba(var(--swatch-menubg-black-color,12, 12, 12),1);
    --woedbar-lvl6-color: rgba(var(--swatch-primary-bright,140, 25, 26),1);
    /* Class Bar Color */
    --woedbar-class-bar-color: rgb(var(--swatch-menubg-black-color,12, 12, 12));
}

div.scale {
    width: -moz-fit-content;
    width: -webkit-fit-content;
    width: fit-content;
    max-width: 98vw;
    margin: 0 auto;
    padding: 1em 0;
    border: none;
    display: -ms-grid;
    display: grid;
        grid-template-areas:
     "class classimage item";
    -ms-grid-columns: 1.25fr auto 1.9fr;
    grid-template-columns: 1.25fr auto 1.9fr;
    -ms-grid-rows: minmax(3em,5em);
    grid-template-rows: minmax(3em,5em);
    -webkit-box-align: stretch;
    -webkit-align-items: stretch;
       -moz-box-align: stretch;
        -ms-flex-align: stretch;
            align-items: stretch;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
    font-size: clamp(0.4845rem, 0.1609rem + 1.6179vw, 0.9375rem);
}

div.class1 {
    -ms-grid-row: 1;
    -ms-grid-column: 1;
    grid-area: class;
    white-space: nowrap;
    padding-right: 1em;
    display: -ms-grid;
    display: grid;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
        grid-template-areas:
     "."
     "leveltxt"
     "classtxt"
     "." ;
    -ms-grid-rows:
     auto
     min-content
     min-content
     auto;
    grid-template-rows:
     auto
     -webkit-min-content
     -webkit-min-content
     auto;
    grid-template-rows:
     auto
     min-content
     min-content
     auto;
}

div.class1>div {
    color: rgb(var(--swatch-text-general, 12, 12, 12));
    font-family: Oswald;
    font-size: 2.90em;
    line-height: 0.9em;
}

div.class1 .base:nth-of-type(1) {
    display: none;
}

div.class1 .base:nth-of-type(2) {
    display: inline;
}

div.class1 .CN:nth-of-type(1) {
    display: inline;
}

div.class1 .CN:nth-of-type(2) {
    display: none;
}

div.class1 .false:nth-of-type(1) {
    display: none !important;
}

div.class1 .false:nth-of-type(2) {
    display: inline !important;
}

div.level-text {
    -ms-grid-row: 2;
    -ms-grid-column: 1;
    grid-area: leveltxt;
}

div.class-text {
    -ms-grid-row: 3;
    -ms-grid-column: 1;
    grid-area: classtxt;
}

div.class1image {
    -ms-grid-row: 1;
    -ms-grid-column: 2;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    grid-area: classimage;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    overflow: hidden;
    margin: 0 0.6em -10px 0;
    padding-right: 0.6em;
}

/* middle images */
div.classified-bar {
    position: relative;
    -webkit-mask-image: url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3Csvg version='1.2' baseProfile='tiny' id='classified-bar' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 10 50' overflow='visible' xml:space='preserve'%3E%3Cpolygon points='5,50 0,50 5,25 0,0 5,0 10,25 '/%3E%3C/svg%3E%0A");
            mask-image: url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3Csvg version='1.2' baseProfile='tiny' id='classified-bar' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 10 50' overflow='visible' xml:space='preserve'%3E%3Cpolygon points='5,50 0,50 5,25 0,0 5,0 10,25 '/%3E%3C/svg%3E%0A");
    -webkit-mask-repeat: no-repeat;
            mask-repeat: no-repeat;
    -webkit-mask-position: center center;
            mask-position: center center;
    display: none;
    width: 0;
    min-width: 1em;
    height: -webkit-calc(100% - 0.5em);
    height: -moz-calc(100% - 0.5em);
    height: calc(100% - 0.5em);
}

div.image-space {
    margin-left: -0.35em;
    display: none;
}

/* display depending on level 0 */
div.class1image[data-level="lv0"] {
    min-width: 0;
    background-image:
     -webkit-linear-gradient(315deg,
     transparent 25%,
     var(--woedbar-lvl2-color) 25%,
     var(--woedbar-lvl2-color) 50%,
     transparent 50%,
     transparent 75%,
     var(--woedbar-lvl2-color) 75%,
     var(--woedbar-lvl2-color) 100%);
    background-image:
     -moz-linear-gradient(315deg,
     transparent 25%,
     var(--woedbar-lvl2-color) 25%,
     var(--woedbar-lvl2-color) 50%,
     transparent 50%,
     transparent 75%,
     var(--woedbar-lvl2-color) 75%,
     var(--woedbar-lvl2-color) 100%);
    background-image:
     -o-linear-gradient(315deg,
     transparent 25%,
     var(--woedbar-lvl2-color) 25%,
     var(--woedbar-lvl2-color) 50%,
     transparent 50%,
     transparent 75%,
     var(--woedbar-lvl2-color) 75%,
     var(--woedbar-lvl2-color) 100%);
    background-image:
     linear-gradient(135deg,
     transparent 25%,
     var(--woedbar-lvl2-color) 25%,
     var(--woedbar-lvl2-color) 50%,
     transparent 50%,
     transparent 75%,
     var(--woedbar-lvl2-color) 75%,
     var(--woedbar-lvl2-color) 100%);
    -webkit-background-size: 5px 5px;
       -moz-background-size: 5px 5px;
         -o-background-size: 5px 5px;
            background-size: 5px 5px;
    padding-right: 0.25em;
}

/* display depending on level */
div.class1image[data-level="lv1"] div:nth-child(-n+2) {
    display: inline-block;
    margin-left: initial;
}

div.class1image[data-level="lv2"] div:nth-child(-n+4) {
    display: inline-block;
}

div.class1image[data-level="lv3"] div:nth-child(-n+6) {
    display: inline-block;
}

div.class1image[data-level="lv4"] div:nth-child(-n+8) {
    display: inline-block;
}

div.class1image[data-level="lv5"] div:nth-child(-n+10) {
    display: inline-block;
}

div.class1image[data-level="lv6"] div:nth-child(-n+12) {
    display: inline-block;
}

/* color depending on level */
div.class1image div.classified-bar:nth-of-type(1) {
    background-color: var(--woedbar-lvl1-color);
}

div.class1image div.classified-bar:nth-of-type(3) {
    background-color: var(--woedbar-lvl2-color);
}

div.class1image div.classified-bar:nth-of-type(5) {
    background-color: var(--woedbar-lvl3-color);
}

div.class1image div.classified-bar:nth-of-type(7) {
    background-color: var(--woedbar-lvl4-color);
}

div.class1image div.classified-bar:nth-of-type(9) {
    background-color: var(--woedbar-lvl5-color);
}

div.class1image div.classified-bar:nth-of-type(11) {
    background-color: var(--woedbar-lvl6-color);
}

div.classified-bar img.sizing-image {
    width: 100%;
    height: 100%;
    visibility: hidden;
}

div.item1 {
    -ms-grid-row: 1;
    -ms-grid-column: 3;
    grid-area: item;
    white-space: nowrap;
    text-align: right;
    display: -ms-grid;
    display: grid;
        grid-template-areas:
     "."
     "itemnum"
     "objclass"
     ".";
    -ms-grid-rows:
     auto
     min-content
     min-content
     auto;
    grid-template-rows:
     auto
     -webkit-min-content
     -webkit-min-content
     auto;
    grid-template-rows:
     auto
     min-content
     min-content
     auto;
}

div.item1 > div {
    color: rgb(var(--swatch-text-general, 12, 12, 12));
    font-family: Oswald;
    font-size: 2em;
    font-weight: normal;
    text-align: center;
    line-height: 1.15;
}

.PL div>span {
    font-size: 0.75em;
}

.ES div>span {
    font-size: 0.85em;
}

div.item1 .PL-top {
    font-size: 1rem;
}

div.item1 .PL-btm {
    font-size: 2rem;
}

div.itemnum {
    -ms-grid-row: 2;
    -ms-grid-column: 1;
    grid-area: itemnum;
}

div.itemnum.TH {
    line-height: 1.5;
    margin-top: -0.5em;
}

div.objclass {
    -ms-grid-row: 3;
    -ms-grid-column: 1;
    --bar-color: rgb(var(--swatch-menubg-black-color, 12, 12, 12));
    grid-area: objclass;
    position: relative;
    -webkit-box-flex: 2 !important;
    -webkit-flex-grow: 2 !important;
       -moz-box-flex: 2 !important;
        -ms-flex-positive: 2 !important;
            flex-grow: 2 !important;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
        -ms-flex-align: center;
            align-items: center;
}
