            mask: radial-gradient(circle at 0.75em center, transparent 0.85em, #000 -moz-calc(0.85em + 1px) 100%) 50% 50%/100% 100% no-repeat;
            mask: radial-gradient(circle at 0.75em center, transparent 0.85em, #000 calc(0.85em + 1px) 100%) 50% 50%/100% 100% no-repeat;
    position: relative;
}
/* Keter Styling */
div.scale[class*="Кетер" i] div.objclass, div.scale[class*="КЕТЕР" i] div.objclass {
    --bar-color: var(--woedbar-keter-color);
}

