    --mask-url: url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3Csvg version='1.2' baseProfile='tiny' id='meter' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 144 144' overflow='visible' xml:space='preserve'%3E%3Cpath d='M72,0C32.24,0,0,32.24,0,72c0,39.76,32.24,72,72,72c39.76,0,72-32.24,72-72C144,32.24,111.76,0,72,0z M107.15,65.25 l-1.7,0.4l-0.6-2.7C105.75,63.65,106.55,64.45,107.15,65.25z M96.45,29.95l-8.2,1.8l-2.1-9.5C90.75,22.95,94.55,25.85,96.45,29.95z M97.75,36.55v7.1l-13.7,3.1v-6.2c0-0.2,0-0.5-0.1-0.9L97.75,36.55z M83.95,53.75l11.2-2.5l5.1,22.6l10-2.2 c0.6,1.83,0.5,4.95,0.5,6.9l-15.6,3.5l-5.1-22.6l-22.6,5.1l-1.1-5h17.6V53.75z M79.05,22.05l3.5,14.9c-1.65-1.66-5.29-1.13-7.4-1.2 l-2.3-10.1l-22.6,5.1l-0.9-4C54.33,19.34,71.65,22.77,79.05,22.05z M45.95,38.65l21.7-4.8l0.4,1.9c-5.15-0.53-9.13,0.89-8.4,6.9 l-13.7,3.1V38.65z M60.45,115.95l-1.6-7.1l-19.1,4.3c-8.81-5.09-6.06-21.06-6.5-29.4l3.2-0.7l5,22.6l22.6-5.1l3.6,15.4H60.45z M98.35,115.65l-1.3-5.9l-22.6,5.1l-5.1-22.6l-22.6,5.1l-5.1-22.6l-8.4,1.9c-0.09-2.41,0.15-5,1-7.2l12.6-2.8l5.1,22.6l22.4-5.1 l5.1,22.6l22.6-5.1l2.7,11.2C102.85,114.25,100.75,115.25,98.35,115.65z M110.05,105.45l-2.7-11.9l-22.6,5.1l-5.1-22.6l-22.6,5 l-5-22.6l-6.1,1.39c0,0,0,0.01,0,0.01c-0.6,0.1-1.2,0.3-1.8,0.4l1.8-0.41c0-1.7,0-5.36,0-7.09l11.2-2.6l5.1,22.6l22.6-5.1l5.1,22.6 l20.7-4.6h0.1C110.54,89.14,111.34,102.31,110.05,105.45z'/%3E%3C/svg%3E%0A");
}
