    --mask-url: url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3Csvg version='1.2' baseProfile='tiny' id='Euclid' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 144 144' overflow='visible' xml:space='preserve'%3E%3Cpath d='M72,0C32.24,0,0,32.24,0,72c0,39.76,32.24,72,72,72c39.76,0,72-32.24,72-72C144,32.24,111.76,0,72,0z M97.8,36.17 l-13.7,13.7v-9.5c0,0,0-0.1,0-0.2l11.7-11.7C97.21,30.72,97.89,33.4,97.8,36.17z M84.2,21.87c2.5,0,4.8,0.6,6.8,1.8l-11.9,11.9h-9.7 l13.8-13.7H84.2z M46,39.67l17.8-17.8h9.7L46,49.37V39.67z M33.7,71.37c0.64-0.26,11.37-12.33,12.3-11.7c0-0.13,0-0.56,0-0.7 l13.7-13.7v9.7l-26.4,26.4C33.4,78.99,33,73.64,33.7,71.37z M33.3,91.07l31.6-31.6h9.7l-41.3,41.3C33.3,100.5,33.3,91.35,33.3,91.07 z M35.4,108.27l62.4-62.4v9.7l-57.7,57.7C38.2,111.97,36.6,110.27,35.4,108.27z M47.2,115.77l54.8-54.8c2.1,1,3.8,2.4,5.3,4.1 l-50.8,50.8C55.51,115.87,48,115.92,47.2,115.77z M110.7,100.27l-15.5,15.6h-9.7l25.2-25.3V100.27z M110.7,80.97l-34.8,34.9h-9.7 l44.2-44.2C110.9,73.7,110.64,78.81,110.7,80.97z'/%3E%3C/svg%3E%0A");
