[[table class="scale [[#if {$lang} | {$lang}-base |  ]]"]]
[[row]]
[[cell class="class1"]]
+* [[span class="base {$trans-level} RU"]]{$lv}/{$item} [[/span]][[#if {$trans-level} | {$trans-level} | ДОПУСК ]][[span class="base {$trans-level} RU"]] {$lv}/{$item}[[/span]]
+* [[#if {$trans-classified} | {$trans-classified} | СЕКРЕТНО]]
[[/cell]]

[[cell rowspan="2" class="class1image"]]
[[image http://scpfoundation.net/local--files/component%3Aclassified-decoration-base/classified-lv{$lv}.png link=#]]
[[/cell]]

[[cell class="item1 RU"]]
+* [[#if {$langRU} |  | Объект №: SCP-{$item} ]]
+* [[#if {$langRU} |  | Класс объекта: {$oc} ]]
[[/cell]]
[[/row]]
[[/table]]

[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Noto+Serif+KR');
@import url(https://indestructibletype-fonthosting.github.io/renner.css);

.class1 .base:nth-of-type(1) {
  display: none;
}
.class1 .base:nth-of-type(2) {
  display: inline;
}

.class1 .CN:nth-of-type(1) {
  display: inline;
}
.class1 .CN:nth-of-type(2) {
  display: none;
}

.class1 .false:nth-of-type(1) {
  display: none!important;
}
.class1 .false:nth-of-type(2) {
  display: inline!important;
}


.class1 {
    white-space: nowrap;
    width: auto;
}

.class1 h1 {
    color: #333;
    font-family: 'Sans Normalcy', sans-serif;
    font-size: 240%;
    margin-top: 0.55em;
}

.class1image img {
    width:60%;
    height: auto;
    max-width: initial;
}

.item1 {
    white-space: nowrap;
    text-align: right;
}

.item1 h1 {
    color: #333;
    font-family: 'renner', Arial, "游明朝", YuMincho, "ヒラギノ明朝 ProN W3", "Hiragino Mincho ProN", "HG明朝E", "ＭＳ Ｐ明朝", "ＭＳ 明朝", 'Noto Serif KR', serif;
    font-size: 160%;
    font-weight: normal;
    transform: scale(0.9, 1);
}

.PL h1 > span {
    font-size: 75%;
}
.ES h1 > span {
    font-size: 85%;
}

.scale tr {
    line-height: 10px;
}

.scale {
    margin: 0;
    padding: 0;
    border: none;
    width: 100%;
    max-width: 100% !important;
}

@media (max-width: 766px) {
.class1 h1 {
    font-size: 3.8vw;
    font-weight: bold!important;
    font-family: 'Sans Normalcy', sans-serif;
}
.class1image img {
    width:140%;
    height: auto;
}
.item1 h1 {
    font-size: 2.2vw;
}
}
@media (min-width: 767px) and (max-width: 960px) {
.class1 h1 {
    font-size: 2.5vw;
    font-weight: bold!important;
    font-family: 'Sans Normalcy', sans-serif;
}
.class1image img {
    width:140%;
    height: auto;
}
.item1 h1 {
    font-size: 1.4vw;
}
}


[[/module]]
