[[module css]]

.document {
display: inline-block;
border-radius: 10px;
border:solid 1px #ccc;
background:#f0f0f0;
float:top; width:96.5%;
padding: 10px;
box-shadow: 0 2px 4px rgba(68, 68, 68, .5);
}

.click{
cursor: pointer;
}

.ping,
.fakedl{
color: #39e;
cursor: pointer;
font-weight: bold;
}

.edit{
color: #666;
font-size: 75%;
}

.edit:hover{
cursor: default;
title: "test";
}

.ping:hover{
text-decoration: underline;
}

.member{
color: #f33;
}

.member0{
color: #fff;
}

.member2{
color: #1f1;
}

.member3{
color: #838;
}

.member:hover,
.member0:hover,
.member2:hover,
.member3:hover,
.fakedl:active{
text-decoration: underline;
cursor: pointer;
}

.chat blockquote,
.chat .blockquote {
color: #eee;
background-color: #223;
border: solid 1px #444;
}

span.member:active,
span.member0:active,
span.member2:active,
span.member3:active,
span.ping:active,
span.click:active{
background-color: #99f;
border-radius: 4px;
}

span.link a{
color: #39e;
}

.newmes{
color: #d00;
}

.red-hr > hr{
background-color: #d00;
bottom: 0.2em;
}

[[/module]]
