[!-- Эта статья создана с помощью FTML Editor - удобного и функцианального расширения для vscode --]

[[include theme:russian-art-hub]]

[[module CSS]]
.ez-gallery {
    display: flex;
    flex-wrap: wrap;
    padding: 0 4px;
    margin: 1em;
    justify-content: space-evenly;
}

.ez-gallery > div {
    margin-top: auto;
    margin-bottom: auto;
}
[[/module]]

[[if {$wide}]]
[[include component:toggle-sidebar]]
[[module CSS]]
#main-content {
    max-width: 90% !important;
}

.scp-image-block {
    margin-left: auto !important;
    margin-right: auto !important;
}
[[/module]]
[[/if]]

[[if {$header}]]
[[module css]]
#header h1 a::before {
    content: '{$header}';
    color: white;
    text-shadow: 1px 1px 0px black;
}

#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}
[[/module]]
[[/if]]

[[if {$subheader}]]
[[module css]]
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
 
#header h2 span::before {
    content: '{$subheader}';
    color: #f0f0c0;
    text-shadow: 1px 1px 1px #000000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
[[/module]]
[[/if]]
