<body>
<div class="svg-container" style="max-width:{$width};">
    <svg preserveAspectRatio="xMidYMin meet">
     <defs>
     <filter id="feDisplacementMap"
     filterUnits="userSpaceOnUse" x="0" y="0" width="100%" height="100%">
     <feTurbulence type="fractalNoise" baseFrequency="0.{$fractal-size}" numOctaves="1" seed="1" stitchTiles="noStitch" result="img"/>
     <feDisplacementMap in="SourceGraphic" in2="img" xChannelSelector="R" yChannelSelector="G" scale="600">
     <animate attributeName="scale"
     values="600;0;0" keyTimes="0;0.75;1" keySpines="0.4, 0.0, 0.2, 1" begin="mask.mouseover" dur="{$speed}ms" fill="freeze"/>
     <animate attributeName="scale"
     values="0;600;600" keyTimes="0;0.75;1" keySpines="0.4, 0.0, 0.2, 1" begin="mask.mouseout" dur="{$speed}ms" fill="freeze"/>
     </feDisplacementMap>
     </filter>
     </defs>
     <g filter="url(#feDisplacementMap)">
     <image xlink:href="{$url}" width="100%" height="100%"/>
     </g>
     <rect id="mask" x="0px" y="0px" width="100%" height="100%" opacity="0"/>
    </svg>
    <img id="imageNoiseBase" width="100%" style="max-width:{$width};" src="{$url}" {$alt}="{$alt-text}"/>
</div>
<script>
    const inputURL = "{$url}";
    const baseImage = document.getElementById("imageNoiseBase");
    const svgImageBase = document.querySelectorAll("svg image")[0];
    try {
     let testURL;
     testURL = new URL(inputURL);
    } catch (_) {
     var imageURL = new URL("{$url}", "https://files.scpfoundation.net/local--files/%%this|fullname%%/").href;
     baseImage.src = imageURL;
     svgImageBase.setAttribute("xlink:href", imageURL);
    }
    if (location.protocol === 'http:') {
        baseImage.src = baseImage.src.replace(/^https:\/\//i, 'http://');
    }
    scaleSVG = () => {
     const svgImage = document.getElementsByTagName("svg")[0];
     const imageNoiseBase = document.getElementById("imageNoiseBase");
     const imageRatio = imageNoiseBase.height / imageNoiseBase.width;
     const svgImageWidthBase = "{$width}";
     let svgImageWidth = parseFloat(svgImageWidthBase.replace(/[^-\d\.]/g, ''));
     let svgImageWidthUnits = svgImageWidthBase.replace(/[0-9]|\./g, "")
     let svgImageHeight = svgImageWidth * imageRatio;

     if(svgImageWidthUnits === "rem") {
     svgImageWidth = svgImageWidth * 16;
     svgImageHeight = svgImageHeight * 16;
     svgImageWidthUnits = "px";
     };
     if(svgImageWidthUnits === "em") {
     let emSize = parseFloat(getComputedStyle(parentElement).fontSize);
     svgImageWidth = svgImageWidth * emSize;
     svgImageHeight = svgImageHeight * emSize;
     svgImageWidthUnits = "px";
     };

     svgImage.setAttribute("width", svgImageWidth + svgImageWidthUnits);
     svgImage.setAttribute("height", svgImageHeight + svgImageWidthUnits);
     var svgViewport = "0 0 " + (svgImageWidth).toString() + " " + (svgImageHeight).toString();
     svgImage.setAttribute("viewBox", svgViewport);
    };
    //baseImage.src = baseImage.src + "?_="+(new Date().getTime());
    baseImage.onload = () => {
     scaleSVG();
    };
</script>
