<script src="https://files.scpfoundation.net/local--files/component:image-features-source/pixel-flow.cjs.js"></script>
<script>
    const inputURL = "{$url}";
    const baseImage = document.getElementById("imagePxlBase");
    try {
     let testURL;
     testURL = new URL(inputURL);
    } catch (_) {
     var imageURL = new URL("{$url}", "https://files.scpfoundation.net/local--files/%%this|fullname%%/").href;
     baseImage.src = imageURL;
    }
    if (location.protocol === 'http:') {
        baseImage.src = baseImage.src.replace(/^https:\/\//i, 'http://');
    }
    createPixelImage = () => {
     const imagePxlParent = document.getElementById("imagePxlParent");
     const imagePxl = document.getElementById("imagePxlBase");
     const imageRatio = imagePxl.width/imagePxl.height;
     if (Modernizr.canvas) {
     let pixelImage = imagePxl;
     let pixelFlows = new PixelFlow(pixelImage, { resolution: {$pixel-res} });

     imagePxlParent.addEventListener("mouseenter", function(){       
     pixelFlows.simpleanimate(0, {$speed});
     });
     imagePxlParent.addEventListener("mouseleave", function(){       
     pixelFlows.simpleanimate({$pixel-res}, {$speed});
     });
     pixelFlows.height = pixelFlows.width * imageRatio;
     } else {
     imagePxl.classList.add("hidden");
     }
    };
    //baseImage.src = baseImage.src + "?_="+(new Date().getTime());
    baseImage.onload = () => {
     createPixelImage();
    };
