[[module CSS]]
.enlarge{
transition: .25s;
}

.enlarge:hover{
transform: scale(1.6);
transition: .25s;
transition-delay: .4s;
z-index: 1000;
}

[[/module]]

[[div class="scp-image-block block-{$align}" style="width:{$width};"]]
[[image {$name} class="enlarge" style="width:{$width};" link={$link}]]
[[div class="scp-image-caption" style="width:{$width};"]]
{$caption}
[[/div]]
[[/div]]
