[[div_ class="scp-image-block block-{$float}" style="width: {$width}; min-height: {$height};"]]
[[html]]

    <!-- Sistema di mapping CC usato: Leaflet -->
    <!-- https://leafletjs.com/examples/quick-start/ -->
    <head>
        <!-- ▼ ▼ ▼ Non toccate nulla qui! ▼ ▼ ▼ -->
        <!-- Leaflet's CSS -->
        <link rel="stylesheet"
            href="https://unpkg.com/leaflet@1.5.1/dist/leaflet.css"
            integrity="sha512-xwE/Az9zrjBIphAcBb3F6JVqxf46+CDLwfLMHloNu6KEQCAWi6HcDUbeOfBIptF7tcCzusKFjFw2yuvEpDL9wQ=="
            crossorigin=""/>

        <!-- Make sure you put this AFTER Leaflet's CSS -->
        <script src="https://unpkg.com/leaflet@1.5.1/dist/leaflet.js"
            integrity="sha512-GffPMF3RvMeYyc1LWMHtK8EbPv0iNZ8/oTtHPx9/cc2ILxQ+u905qIwdpULaqDkyBKgOaB57QTMg7ztg8Jm2Og=="
            crossorigin=""></script>

        <!-- script per Map Stamen -->
        <script type="text/javascript" src="/local--files/component:minimap-base/tile.stamen.js"></script>
        <!-- ▲ ▲ ▲ Non toccate nulla qui! ▲ ▲ ▲ -->
    </head>


    <body>

            <!-- ▼ ▼ ▼ Non toccate nulla qui! ▼ ▼ ▼ -->
            <!-- Dimensioni della mappa -->
            <div id="mapid" style="min-height: {$height}; height: {$height}; box-sizing:border-box;">
            </div>
            <!-- ▲ ▲ ▲ Non toccate nulla qui! ▲ ▲ ▲ -->

            <script type="text/javascript">
                // Dati su come viene visualizzata la mappa appena viene caricata
                var Latitude = {$latitude}
                var Longitude = {$longitude}
                var Zoom = {$zoom}  // Zoom iniziale della mappa
                if (Zoom < 3) { Zoom = 3; }
                if (Zoom > 17) { Zoom = 17; }
                var MinZoom = Zoom-2 // Valore minimo di zoom
                var MaxZoom = Zoom+2 // Valore massimo di zoom


                // ▼ ▼ ▼ Non toccate nulla qui! ▼ ▼ ▼
                var mymap = L.map('mapid', {attributionControl: false, zoomControl: false })
                             .setView([Latitude + {$latviewoffset},
                                       Longitude + {$lonviewoffset}], Zoom)
                mymap.options.minZoom = MinZoom
                mymap.options.maxZoom = MaxZoom

                // Tile servers: https://wiki.openstreetmap.org/wiki/Tile_servers

                // Sistema di tile CC usato: Maps Stamen
                // Tipi di visualizzazione: "toner", "terrain", "watercolor"
                mymap.addLayer(
                    new L.StamenTileLayer(
                        "{$visualstyle}",
                        {detectRetina: true}
                    )
                )

                if ({$crosshair})
                {
                    var lw = 1.0;
                    var lat_line = L.polyline([ [-85.05112878, Longitude], [85.05112878, Longitude] ],
                                              {color: "{$color}", weight: lw}).addTo(mymap);

                    var long_line = L.polyline([ [Latitude, -180.0], [Latitude, 180.0] ],
                                               {color: "{$color}", weight: lw}).addTo(mymap);

                    var circle = L.circleMarker([Latitude, Longitude],
                                                {color: "{$color}", radius: 17,
                                                 fill: false, weight: lw}
                                 ).addTo(mymap);  
                }

            </script>

    </body>

[[/html]]\
[!-- CAPTION HERE --]
[[div class="scp-image-caption" style="width: 100%;"]]
    {$caption}
[[/div]]
[[/div]]
