[[module CSS]]
.new-page-form {
  border: 1px solid #ccc;
  background: #eee;
  padding: 8px;
  text-align: center;
}
.new-page-form form {
  display: flex;
  flex-direction: column;
}
.new-page-form p {
  margin: 4px 0;
}
.new-page-form p div {
  margin: 4px 0;
}
[[/module]]

[[div class="new-page-form"]]
[[form target="component:new-page"]]
[[div]]Название страницы:[[/div]]
[[if {$example}]]
[[input name="new_fullname" type="text" placeholder="например, {$example}" required="true"]]
[[else]]

[[input name="new_fullname" type="text" placeholder="например, scp-1432-ru" required="true"]]
[[/if]]

[[input style="display: none" type="hidden" name="category" value="{$category}"]]

[[if {$submit}]]
  [[input value="{$submit}" type="submit"]]
[[else]]
  [[input value="Создать" type="submit"]]
[[/if]]

[[/form]]
[[/div]]

[[if %%path|new_fullname%%]]
  [[if %%path|category%%]]
    [[module Redirect destination="/%%path|category%%:%%path_url|new_fullname%%/edit/true"]]
  [[else]]
    [[module Redirect destination="/%%path_url|new_fullname%%/edit/true"]]
  [[/if]]
[[/if]]
