[[code type="css"]]
a {
    text-decoration: none;
    color: #b01;
}
.bar {
    margin: 10px auto;
}
.bar, .init-button {
    font-family: Consolas, Menlo, 'Ubuntu Mono', monospace;
}
.content {
    display: none;
    font-family: sans-serif;
}
.loading-text {
    display: none;
    margin: 10px auto;
}
.init-button {
    color: #b01;
    font-size: 14px;
    border-radius: 2px;
}
.init-button:hover {
    cursor: pointer;
}
.wikidot-text {
    font-size: 14px;
    font-family: verdana,arial,helvetica,sans-serif;
}
[[/code]]
