        [[/div]]
        [[span class="apcs-tooltip"]]
          {$danger-select-1}
        [[/span]]
      [[/div]]
     [[/if]]\
     [[if {$danger-select-2}]]
      [[div_ data-hazard-type="{$danger-select-2}"]]
