        [[/div]]
        [[span class="apcs-tooltip"]]
          {$danger-select-2}
        [[/span]]
      [[/div]]
     [[/if]]\
     [[if {$danger-select-3}]]
      [[div_ data-hazard-type="{$danger-select-3}"]]
        [[div_ style="background-image: url('https://scpfoundation.net/local--files/component%3As7-apcs/{$danger-select-3}.png')" class="apcs-hazard"]]
        [[/div]]
        [[span class="apcs-tooltip"]]
          {$danger-select-3}
        [[/span]]
      [[/div]]
     [[/if]]\
     [[if {$danger-select-4}]]
      [[div_ data-hazard-type="{$danger-select-4}"]]
        [[div_ style="background-image: url('https://scpfoundation.net/local--files/component%3As7-apcs/{$danger-select-4}.png')" class="apcs-hazard"]]
        [[/div]]
        [[span class="apcs-tooltip"]]
          {$danger-select-4}
        [[/span]]
      [[/div]]
     [[/if]]\
     [[if {$danger-select-5}]]
      [[div_ data-hazard-type="{$danger-select-5}"]]
        [[div_ style="background-image: url('https://scpfoundation.net/local--files/component%3As7-apcs/{$danger-select-5}.png')" class="apcs-hazard"]]
        [[/div]]
        [[span class="apcs-tooltip"]]
          {$danger-select-5}
        [[/span]]
      [[/div]]
     [[/if]]\
     [[if {$danger-select-6}]]
      [[div_ data-hazard-type="{$danger-select-6}"]]
        [[div_ style="background-image: url('https://scpfoundation.net/local--files/component%3As7-apcs/{$danger-select-6}.png')" class="apcs-hazard"]]
        [[/div]]
        [[span class="apcs-tooltip"]]
          {$danger-select-6}
        [[/span]]
      [[/div]]
     [[/if]]
    [[/div]]
    [[div_ class="apcs-class {$scp-class}" data-non-standard="{$non-standart-class}" data-non-standard-tooltip="{$scp-class}" style="--custom-color: {$non-standart-color}"]]
      [[span class="apcs-tooltip"]]
        Класс —
        [[span class="apcs-class-name" data-non-standard-tooltip="{$scp-class}"]]
        [[/span]]
      [[/span]]
    [[/div]]
  [[/div]]
  [!-- код продублирован из-за хака. сорян за количество дивов :p --]
  [[div_ class="apcs-info"]]
    [[div_ class="apcs-info-activation"]]
      {$activation}
    [[/div]]
    [[div_ class="apcs-info-risk risk-{$contaiment-risk}" data-containment-risk="{$contaiment-risk}" data-k-class="{$k-class}"]]
    [[/div]]
    [[div_ class="apcs-info-vedist vedist-{$threat-ind}"]]
    [[/div]]
    [[div_ class="apcs-info-number"]]
      {$scp-number}
    [[/div]]
  [[/div]]
  [[div_ class="apcs-info apcs-info-tooltips"]]
    [[div_ class="apcs-info-activation"]]
      {$activation}
    [[/div]]
    [[div_ class="apcs-info-risk risk-{$contaiment-risk}" data-containment-risk="{$contaiment-risk}" data-k-class="{$k-class}"]]
      [[span class="apcs-tooltip"]]
      [[/span]]
    [[/div]]
    [[div_ class="apcs-info-vedist vedist-{$threat-ind}"]]
      [[span class="apcs-tooltip"]]
      [[/span]]
    [[/div]]
    [[div_ class="apcs-info-number"]]
      {$scp-number}
    [[/div]]
  [[/div]]
[[/div]]
