[[module CSS]]
.tape-record .record {
    animation-duration: calc(1s * var(--duration, 2.5));
    animation-delay: calc(1s * var(--delay));
}

.tape-wrap .collapsible-block-unfolded-link .collapsible-block-link {
    animation-delay: calc(1s * var(--total-duration) + 2s);
}

.tape-record {
    position: relative;
    height: 6em;
    pointer-events: none;
}
.record {
    display: block;
    position: absolute;
    width: 100%;
    animation-name: record;
    opacity: 0;
    top: 0;
    text-align: center;
}
.tape-wrap {
    position: relative;
    background-color: rgba(244, 244, 244, 1);
    border: 1px dashed #999;
    padding: 0 1em;
}
.tape-anim {
    position: relative;
    background-image: url("http://scpfoundation.net/local--files/component:tape/tape.png");
    background-size: 200px;
    background-position: center center;
    background-repeat: no-repeat;
    min-height: 300px;
}
.tape-left,.tape-right {
    display: block;
    position: absolute;
    margin: auto;
    height: 0;
    width: 70px;
    padding-bottom: 100%;
    background-image: url("http://scpfoundation.net/local--files/component:tape/scp-trans.png");
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat;
    pointer-events: none;
}
.tape-left {
    top: -20px;
    bottom: 0;
    left: -70px;
    right: 0;
    animation-name: spin1;
    animation-duration: 1s;
    animation-timing-function: linear;
    animation-fill-mode: forwards;
    animation-iteration-count: calc(var(--total-duration) - .1)
}
.tape-right {
    top: 0;
    bottom: -32px;
    left: 0;
    right: -72px;
    animation-name: spin1;
    animation-duration: 1s;
    animation-timing-function: linear;
    animation-fill-mode: forwards;
    animation-iteration-count: var(--total-duration)
}
.tape-wrap .collapsible-block-unfolded-link {
    padding-top: 1em;
}
.tape-wrap .collapsible-block-folded,.tape-wrap .collapsible-block-unfolded-link {
    position: relative;
    text-align: center;
}
.tape-wrap .collapsible-block-link:before {
    display: inline-block;
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    transform: translate(0, 0);
}
.tape-wrap .collapsible-block-folded .collapsible-block-link:before {
    content: "\f04b";
}
.tape-wrap .collapsible-block-unfolded-link .collapsible-block-link:before {
    content: "\f1da";
}
.tape-wrap .collapsible-block-unfolded-link .collapsible-block-link {
    animation-name: g-ex;
    animation-duration: 1s;
    animation-fill-mode: forwards;
    visibility: hidden;
    opacity: 0;
}
.tape-wrap .collapsible-block-folded {
    padding-top: 1em;
    padding-bottom: 1em;
}
@keyframes g-ex {
    50% {
        visibility: visible;
        opacity: 0;
    }
    100% {
        visibility: visible;
        opacity: 1;
    }
}
@keyframes spin1 {
    0% {
        transform: rotate(0deg);
    }
    100% {
        transform: rotate(360deg);
    }
}
@keyframes record {
    0% {
        opacity: 0;
    }
    25% {
        opacity: 1;
    }
    75% {
        opacity: 1;
    }
    100% {
        opacity: 0;
    }
}

[[/module]]

[[ifexpr "%%this|fullname%%"=="component:tape-style"]]
[[iftags визуальный_элемент]]
[[module Redirect destination="/component:tape"]]
[[/iftags]]
[[/ifexpr]]
