[[module CSS]]
#page-content .{$class} .collapsible-block {
    margin: 16px 0;
    border: {$border};
    background: {$bg-color};
    box-shadow: 0 2px 3px #ccc;
    transition: all 0.3s ease;
    position: relative;
}
#page-content .{$class} .collapsible-block:hover {
    border: {$hover-border};
}
#page-content .{$class} .collapsible-block-content {
    margin: 16px 24px;
}
#page-content .{$class} .collapsible-block-unfolded-link {
    text-align: center;
}
#page-content .{$class} .collapsible-block-folded {
    text-align: center;
}
#page-content .{$class} .collapsible-block-link {
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;
    padding: 18px 24px;
    display: block;
    z-index: 3;
    position: relative;
    color: {$color};
}
#page-content .{$class} .collapsible-block .collapsible-block {
    border: none;
    box-shadow: none;
}
#page-content .{$class} .collapsible-block-content .collapsible-block-content {
    margin: 0px;
}
[[/module]]

[[if {$special}]]
[[module CSS]]
#page-content .{$class} .collapsible-block-folded::before {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    width: 28px;
    height: 100%;
    background: {$color};
    clip-path: polygon(0 0, 0% 100%, 80% 50%);
    z-index: 5;
}
#page-content .{$class} .collapsible-block-unfolded::before {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    width: 28px;
    height: 51px;
    background: {$color};
    clip-path: polygon(0 0, 0% 100%, 80% 50%);
    z-index: 5;
}
#page-content .{$class} .collapsible-block-folded::after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    width: 28px;
    height: 100%;
    background: {$color};
    clip-path: polygon(100% 0, 20% 50%, 100% 100%);
    z-index: 5;
}
#page-content .{$class} .collapsible-block-unfolded::after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    width: 28px;
    height: 51px;
    background: {$color};
    clip-path: polygon(100% 0, 20% 50%, 100% 100%);
    z-index: 5;
}
#page-content .{$class} .collapsible-block-unfolded {
    border-left: 3px solid {$color};
    border-right: 3px solid {$color};
    position: relative;
    padding-bottom: 14px;
}
[[/module]]

[[/if]]

[[if {$all-spoilers}]]
[[module CSS]]
#page-content .collapsible-block {
    margin: 16px 0;
    border: {$border};
    background: {$bg-color};
    box-shadow: 0 2px 3px #ccc;
    transition: all 0.3s ease;
    position: relative;
}
#page-content .collapsible-block:hover {
    border: {$hover-border};
}
#page-content .collapsible-block-content {
    margin: 16px 24px;
}
#page-content .collapsible-block-unfolded-link {
    text-align: center;
}
#page-content .collapsible-block-folded {
    text-align: center;
}
#page-content .collapsible-block-link {
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;
    padding: 18px 24px;
    display: block;
    z-index: 99;
    position: relative;
    color: {$color};
}
#page-content .collapsible-block .collapsible-block {
    border: none;
    box-shadow: none;
}
#page-content .collapsible-block-content .collapsible-block-content {
    margin: 0px;
}
[[/module]]
[[/if]]

[[if {$animation}]]
[[module CSS]]
#page-content .{$class} .collapsible-block-folded .collapsible-block-link:hover::before {
  background: {$color};
  width: 100%;
}
#page-content .{$class} .collapsible-block-folded .collapsible-block-link:hover::after {
  background: {$color};
  width: 100%;
}
#page-content .{$class} .collapsible-block-folded .collapsible-block-link {
  transition: .3s all;
}
#page-content .{$class} .collapsible-block-folded .collapsible-block-link:hover {
  color: white;
}
#page-content .{$class} .collapsible-block-folded .collapsible-block-link::before {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    width: 1px;
    background-color: {$color};
    transition: .3s all;
    z-index: -5;
}
#page-content .{$class} .collapsible-block-folded .collapsible-block-link::after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    height: 100%;
    width: 1px;
    background-color: {$color};
    transition: .3s all;
    z-index: -5;
}
#page-content .{$class} .collapsible-block-unfolded .collapsible-block-link {
    text-align: center;
    background: {$color};
    color: white;
}
#page-content .{$class} .collapsible-block-unfolded {
    position: relative;
    padding-bottom: 14px;
    border: none !important;
}
[[/module]]
[[/if]]

[[if {$button-color}]]

[[module CSS]]
#page-content .{$class} .collapsible-block-link {
    background: {$button-color};
}
[[/module]]

[[/if]]

[[if {$button-text-color}]]

[[module CSS]]
#page-content .{$class} .collapsible-block-link {
  color: {$button-text-color};
}
[[/module]]

[[/if]]

[[if {$button-text-hover}]]

[[module CSS]]
#page-content .{$class} .collapsible-block-folded .collapsible-block-link:hover {
  color: {$button-text-hover};
}
[[/module]]

[[/if]]
[[/iftags]]
