[[module css]]
@media not all and (max-width: 767px) {
#main-content {
    max-width: min(57.5rem,calc(100% - 3rem));
}
}

:root { --box-accent: #3b5c97; }

.tale-list>.list-pages-box {
    display: grid;
    gap: .75rem;
    margin-block: 1.5rem;
}
.tale-list>.list-pages-box .pager { border-block-start: .225rem solid var(--box-accent); }

.tale-block {
    background-color: #fff;
    box-shadow: 0 0 0.325rem rgb(0 0 0 / 0.25);
    contain: paint;
    display: grid;
    grid-template-areas:
        "title date"
        "meta meta";
    grid-template-columns: 1fr max-content;
    gap: .425rem;
    padding: .625rem;
    --box-accent: inherit;
}

.tale-block  .title {
    grid-area: title;
    display: flex;
    gap: .325ex 0.75ex;
    align-items: center;
    flex-wrap: wrap;
}
.tale-block .title > a {
    font-weight: bold;
    font-size: 1.075em;
}
.tale-block .title > .author { font-size: .775em; flex-grow: 1; }
.author .printuser {
    display: inline-flex;
    align-items: baseline;
    gap: .325em;
}
.author .printuser img {
    margin: 0;
    padding: 0;
    padding-left: .475rem;
}

.tale-block .date {
    grid-area: date;
    font-feature-settings: 'case','ss01','ss04','tnum';
    font-size: .725em;
}

.tale-block .meta {
    grid-area: meta;
    display: flex;
    font-size: .775em;
    gap: .75rem;
    justify-content: space-between;
}

.meta .tag-list {
    display: flex;
    flex-wrap: wrap;
    gap: .25rem;
    align-items: flex-start;
}
.tag-list a {
    display: block;
    border-radius: 1.5rem;
    padding: .125em .675em;
    text-decoration: none;
    font-weight: 550;
    color: var(--box-accent);
    background-color: #3b5c970f;
}
.tag-list a:is(:hover,:focus) {
    color: white;
    background-color: var(--box-accent);
}

.meta .sec-info {
    display: grid;
    grid-template-rows: 1rem 1rem;
    align-items: center;
    text-align: end;
    gap: .25rem;
    min-width: max-content;
    align-self: flex-end;
}
.sec-info > br { display: none; }
.sec-info .comments > span { font-size: 0; }

.tale-block .description {
    grid-column: 1/-1;
}
.description > :not(.preview) { display: none; }
.description:not(:has(> .preview)) { display: none; } /* progressive enhancement */
.description > .preview {
    display: block;
    font-size: .9125em;
    padding-block-start: .425rem;
    border-block-start: .05rem solid #ddd;
}
.description > .preview > p:first-child { margin-block-start: 0.25em; }
.description > .preview > p:last-child { margin-block-end: 0.25em; }


div.tag-nav {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: .25rem;
    font-size: .9125em;
    margin-block: .5rem;
}
div.tag-nav br { display: none; }
div.tag-nav p { display: contents; }
.show-spoiler {
  margin: 1rem 0;
}
.show-spoiler .collapsible-block-link {
  background: #DCEDC8;
  padding: 6px;
  border-radius: 1.5rem;
  color: #448000;
}
.show-spoiler .collapsible-block-link:hover {
  text-decoration: none;
  background: #448000;
  color: white;
}
[[/module]]
