    background: white;
    box-shadow: 0 2px 3px #ccc;
    transition: all 0.3s ease;
}
#page-content .collapsible-block:hover {
    border: #505052 solid 1px;
    box-shadow: 0 2px 3px #ccc;
}
#page-content .collapsible-block-content {
    margin: 16px 24px;
}
#page-content .collapsible-block-unfolded-link {
    text-align: center;
}
#page-content .collapsible-block-folded {
    text-align: center;
}
#page-content .collapsible-block-link {
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;
    padding: 18px 24px;
    display: block;
}
/* часть ниже убирает границы спойлера в спойлере */
#page-content .collapsible-block .collapsible-block {
    border: none;
    box-shadow: none;
}
/* а эта убирает отступы внутри спойлера в спойлере */
#page-content .collapsible-block-content .collapsible-block-content {
    margin: 0px;
}
.blt {
    line-height: 1.7;
}
.noted {
  display: block;
  padding: 12px 12px;
  font-family: 'Marck Script', cursive;
  font-size: 1.8em;
  box-shadow: 0 2px 3px #ccc;
  background: url("https://scpfoundation.net/local--files/sandbox:scp-1169-ru/burn-bg.jpg");
  background-size: 99.9%;
  background-position: center;
  clip-path: polygon(8px 0%, calc(100% - 36px) 0%, 100% 8px, 100% calc(100% - 76px), calc(100% - 28px) 100%, 8px 100%, 0% calc(100% - 181px), 0% 8px);
}
.burn-censored {
  border-radius: 5px;
  color: transparent;
  background: url("https://scpfoundation.net/local--files/sandbox:scp-1169-ru/burn-censor.jpg");
  opacity: 10%;
}
.burn-censored::selection {
  opacity: 0%;
}
@media (max-width: 767px) {
.mobile {
    font-size: 90%;
}
}
[[/module]]
