[[html]]
<img id="pic" width="640px" alt="" class="image" />
<script type="text/javascript">
var firstUrl = "http://scpfoundation.net/local--files/fragment%3A2521-xxx-6/Jinn2521XXX-6.png";
var secondUrl = "http://scpfoundation.net/local--files/fragment%3A2521-xxx-6/Jinn2521XXX-7.png";

new Image().src = firstUrl;

new Image().src = secondUrl;

window.onload = function(){

var org = window.document.getElementById('pic');
org.src = firstUrl;
org.onmouseover = function(ev){
this.src = secondUrl;
}
org.onmouseout = function(ev){
this.src = firstUrl;
}

 org.addEventListener('touchstart',org.onmouseover,false);
 org.addEventListener('touchend',org.onmouseout,false);
}
</script>
[[/html]]
