source:
[[module css]]
@import url('https://fonts.googleapis.com/css?family=Asset|Barrio|Eczar:600,700|Kumar+One|Major+Mono+Display|Miltonian+Tattoo|Ultra|Zilla+Slab+Highlight');

.warning-container iframe {
    min-height: 9.375rem;
}

.warning-container,
.warning-container * {
    -webkit-box-sizing: border-box;
     -moz-box-sizing: border-box;
     box-sizing: border-box;
}

.warning-container {
    --wght: 900;
    width: 90%;
    margin: 0 auto;
    background-color: rgb(var(--dark-gray-monochrome));
    color: rgb(var(--swatch-text-light));
    font-weight: 900;
    border: 0.5em solid rgb(var(--swatch-primary-darkest));
}

.warning-container .warning-top {
    font-size: 200%;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    padding: 1rem;
    font-family: var(--title-font);
    background-color: rgb(var(--swatch-primary));
    background-image: url("https://scp-wiki.wikidot.com/local--files/theme:the-foundation-theme/foundation-circles-bg.png");
    -webkit-background-size: 50% 50%;
    -moz-background-size: 50%;
    -o-background-size: 50%;
    background-size: 50%;
    background-attachment: fixed;
    background-position: 50% 50%;
}

.warning-container .warning-body {
    --wght: 300;
    font-weight: 300;
    padding: 1rem;
    margin: 1rem;
    line-height: 1.5;
    font-size: 115%;
    font-family: var(--mono-font);
    background-color: rgb(var(--swatch-primary-darkest),0.25);
}

.warning-container .warning-body,
.warning-container .warning-body * {
    font-variation-settings:
     "wght" var(--mono-wght),
     "slnt" var(--slnt),
     "CASL" var(--CASL),
     "CRSV" var(--CRSV),
     "MONO" var(--MONO);
}

#extra-div-3 {
    z-index: 2;
}

#extra-div-1 {
    background-image: var(--gradient-header), var(--gradient-topmenu);
    -webkit-background-size: 100% 7.5rem, 100% 9.375rem;
     -moz-background-size: 100% 7.5rem, 100% 9.375rem;
     -o-background-size: 100% 7.5rem, 100% 9.375rem;
     background-size: 100% 7.5rem, 100% 9.375rem;
    -webkit-background-size: var(--header-background-image-size), 100% 14.375rem;
     -moz-background-size: var(--header-background-image-size), 100% 14.375rem;
     -o-background-size: var(--header-background-image-size), 100% 14.375rem;
     background-size: var(--header-background-image-size), 100% 14.375rem;
    background-repeat: repeat-x;
    display: block;
    position: absolute;
    width: 100%;
    height: 14.375rem;
    top: 0;
    left: 0;
    z-index: 1;
}

.survey-container + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-content  {
    display: block;
    width: 100%;
    height: 100vh;
    position: relative;
}

.survey-container + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-content > p {
    width: 100%;
    height: 100%
}

.survey-container + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-content > p > iframe {
    width: 100vw;
    height: 100vh;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 10;
}

.page-title {
    --wght: 900;
    margin-top: 1rem;
    min-height: 4rem;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    width: -webkit-calc(100% - 1em - 0.5rem);
    width: -moz-calc(100% - 1em - 0.5rem);
    width: calc(100% - 1em - 0.5rem);
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    border: initial;
    font-size: 3em;
    line-height: 1;
    text-align: center;
    color: rgb(var(--white-monochrome));
    background-color: rgb(var(--swatch-primary));
    padding: 0.5em;
    border: 0.25rem solid rgb(var(--white-monochrome));
    position: relative;
    z-index: 1;
    font-family: var(--title-font);
    font-weight: 900;
}

.page-title::before {
    content: " ";
    pointer-events: none;
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-image: url(https://nu-scptheme.github.io/Black-Highlighter/img/logo.svg);
    background-repeat: no-repeat;
    background-position: center center;
    -webkit-background-size: 100% 100%;
    -moz-background-size: 100%;
    -o-background-size: 100%;
    background-size: 100%;
    background-color: rgb(var(--swatch-primary));
    z-index: -1;
    opacity: 0.25;
}
#page-content {
    position: relative;
    min-height: 125vh;
}

.collapsible-block {
    width: 100%;
}

.collapsible-block-folded > a,
.collapsible-block-unfolded-link > a {
    --wght: 900;
    display: -webkit-inline-box;
    display: -webkit-inline-flex;
    display: -moz-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    width: 100%;
    height: 100%;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    color: rgb(var(--white-monochrome));
    font-weight: 900;
    font-size: 1.25em;
    background: rgb(var(--gray-monochrome));
    -webkit-border-bottom-left-radius: 0.5em;
    -moz-border-radius-bottomleft: 0.5em;
    border-bottom-left-radius: 0.5em;
    -webkit-border-bottom-right-radius: 0.5em;
    -moz-border-radius-bottomright: 0.5em;
    border-bottom-right-radius: 0.5em;
}

.survey-container + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-unfolded-link > a.collapsible-block-link {
    --wght: 900;
    position: fixed;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
     -moz-box-align: center;
     -ms-flex-align: center;
     align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
     -moz-box-pack: center;
     -ms-flex-pack: center;
     justify-content: center;
    width: 2.35rem;
    height: 2.35rem;
    top: 1rem;
    right: 1rem;
    -webkit-border-radius: 50%;
     -moz-border-radius: 50%;
     border-radius: 50%;
    font-size: 2rem;
    font-weight: 900;
    background-color: rgb(var(--white-monochrome));
    color: rgb(var(--swatch-primary));
    cursor: pointer;
    -webkit-transform: rotateZ(0);
     -moz-transform: rotateZ(0);
     -ms-transform: rotate(0);
     -o-transform: rotateZ(0);
     transform: rotateZ(0);
    -webkit-transition: background-color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    transition: background-color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition: background-color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), -o-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    -moz-transition: transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), background-color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), -moz-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    transition: transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), background-color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    transition: transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), background-color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), color 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), -moz-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1), -o-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    z-index: 9999;
    opacity: 1;
}

.survey-container + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-unfolded-link > a.collapsible-block-link:hover {
    -webkit-transform: rotateZ(30deg);
     -moz-transform: rotateZ(30deg);
     -ms-transform: rotate(30deg);
     -o-transform: rotateZ(30deg);
     transform: rotateZ(30deg);
    background-color: rgb(var(--swatch-primary));
    color: rgb(var(--white-monochrome));
    text-decoration: none;
}

.survey-container + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-unfolded-link > a.collapsible-block-link::before {
    --wght: 700;
    content: "SECOND ATTEMPTS WILL BE NOTED.";
    width: 10rem;
    height: auto;
    position: fixed;
    top: 3rem;
    right: 1rem;
    background-color: rgb(var(--black-monochrome)) !important;
    border: 0.25rem double rgb(var(--swatch-primary));
    opacity: 0;
    -webkit-transition:
    opacity 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    -webkit-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
    opacity 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    -webkit-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition:
    opacity 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    -o-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    -moz-transition:
    transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    opacity 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    -moz-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
    transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    opacity 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
    transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    opacity 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    -webkit-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    -moz-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1),
    -o-transform 0.5s cubic-bezier(0.4, 0.0, 0.2, 1);
    -webkit-transform: translateY(-10rem);
     -moz-transform: translateY(-10rem);
     -ms-transform: translateY(-10rem);
     -o-transform: translateY(-10rem);
     transform: translateY(-10rem);
    font-size: 1rem;
    font-weight: 700;
    line-height: 1.25;
    padding: 1rem 1rem 1rem 1rem;
}


.survey-container + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-unfolded-link > a.collapsible-block-link:hover::before {
    -webkit-transform: translateY(0) rotateZ(-30deg);
     -moz-transform: translateY(0) rotateZ(-30deg);
     -ms-transform: translateY(0) rotate(-30deg);
     -o-transform: translateY(0) rotateZ(-30deg);
     transform: translateY(0) rotateZ(-30deg);;
    opacity: 1;
}


.survey-container + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-content {
    height: 100%;
    position: absolute;
}

.recruiting-doc + div.collapsible-block > div.collapsible-block-unfolded > div.collapsible-block-content {
    position: relative;
    width: 100%;
}

table.wiki-content-table {
    width: 100%;
    font-size: 1.5em;
    text-align: center;
    margin: 0 auto;
}

table.wiki-content-table,
table.wiki-content-table * {
    border-color: rgb(var(--black-monochrome)) !important;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

table.wiki-content-table tr:last-of-type {
    --wght: 700;
    width: 100%;
    display: -webkit-inline-box;
    display: -webkit-inline-flex;
    display: -moz-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    -webkit-box-align: stretch;
    -webkit-align-items: stretch;
    -moz-box-align: stretch;
    -ms-flex-align: stretch;
    align-items: stretch;
    margin: 0;
    font-weight: 700;
}

table.wiki-content-table td {
    display: -webkit-inline-box;
    display: -webkit-inline-flex;
    display: -moz-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    margin: 0;
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
    -moz-box-flex: 2;
    -ms-flex-positive: 2;
    flex-grow: 2;
    -webkit-flex-basis: 50%;
    -ms-flex-preferred-size: 50%;
    flex-basis: 50%;
    background-color: rgb(var(--swatch-primary-darkest));
    color: rgb(var(--white-monochrome));
    text-transform: uppercase;
    line-height: 1;
    text-align: center;
}

table.wiki-content-table td * {
    width: 100%;
}

table.wiki-content-table td span:nth-of-type(even) {
    font-family: var(--mono-font);
    font-size: 0.75em;
}

table.wiki-content-table td span:nth-of-type(even),
table.wiki-content-table td span:nth-of-type(even) * {
    font-variation-settings:
     "wght" var(--mono-wght),
     "slnt" var(--slnt),
     "CASL" var(--CASL),
     "CRSV" var(--CRSV),
     "MONO" var(--MONO);
}

#main-content {
    position: initial;
}

div.hansarp_doc {
    /* Typefaces */
    --body-font: 'Eczar', 'Lato', sans-serif;
    --header-font: 'Barrio', sans-serif;
    --title-font: 'Ultra', sans-serif;
    --mono-font: 'Major Mono Display', 'PT Mono', "Andale Mono", "Courier New", Courier, monospace;

    /* Standard Colors */
    --white-monochrome: 223, 188, 115;
    /* white */
    --pale-gray-monochrome: 184, 161, 115;
    /* v light gray for blockquotes and stuff */
    --light-gray-monochrome: 161, 143, 102;
    /* light accent gray for login status */
    --gray-monochrome: 126, 112, 83;
    /* gray */
    --dark-gray-monochrome: 91, 81, 60;
    /* dark accent gray for sidebar background */
    --black-monochrome: 44, 47, 41;
    /* black */
    --bright-accent: 195, 64, 34;
    /* bright red */
    --medium-accent: 172, 65, 40;
    /* medium red */
    --dark-accent: 125, 52, 42;
    /* dark red */
    --newpage-color: 234, 128, 85;
    /* pale orange */

    --swatch-topmenu-bg-color: var(--bright-accent);

    --gradient-header:
     linear-gradient(to top,
     rgba(var(--white-monochrome), 0.07) 0,
     rgba(var(--bright-accent), 0.03) 70%,
     rgba(var(--bright-accent), 0.02) 100%);
    --gradient-topmenu:
     linear-gradient(to bottom,
     rgba(255, 255, 255, 0) 0,
     rgba(255, 255, 255, 0) 7.5rem,
     rgba(var(--swatch-topmenu-border-color), 0) 7.5rem,
     rgba(var(--swatch-topmenu-bg-color), 1) 7.625rem,
     rgba(var(--swatch-topmenu-bg-color), 1) -webkit-calc(100% - .125rem),
     rgba(var(--swatch-topmenu-bg-color), 1) -webkit-calc(100% - .125rem),
     rgba(var(--swatch-topmenu-border-color), 0) 100%);
    --gradient-topmenu:
     linear-gradient(to bottom,
     rgba(255, 255, 255, 0) 0,
     rgba(255, 255, 255, 0) 7.5rem,
     rgba(var(--swatch-topmenu-border-color), 0) 7.5rem,
     rgba(var(--swatch-topmenu-bg-color), 1) 7.625rem,
     rgba(var(--swatch-topmenu-bg-color), 1) -moz-calc(100% - .125rem),
     rgba(var(--swatch-topmenu-bg-color), 1) -moz-calc(100% - .125rem),
     rgba(var(--swatch-topmenu-border-color), 0) 100%);
    --gradient-topmenu:
     linear-gradient(to bottom,
     rgba(255, 255, 255, 0) 0,
     rgba(255, 255, 255, 0) 7.5rem,
     rgba(var(--swatch-topmenu-border-color), 0) 7.5rem,
     rgba(var(--swatch-topmenu-bg-color), 1) 7.625rem,
     rgba(var(--swatch-topmenu-bg-color), 1) calc(100% - .125rem),
     rgba(var(--swatch-topmenu-bg-color), 1) calc(100% - .125rem),
     rgba(var(--swatch-topmenu-border-color), 0) 100%);
    --gradient-topmenu-mobile:
     linear-gradient(to bottom,
     rgba(var(--swatch-topmenu-border-color), 0) 0,
     rgba(var(--swatch-topmenu-bg-color), 1) 0.125rem,
     rgba(var(--swatch-topmenu-bg-color), 1) -webkit-calc(3rem - 0.125rem),
     rgba(var(--swatch-topmenu-bg-color), 1) -webkit-calc(3rem - 0.125rem),
     rgba(var(--swatch-topmenu-border-color), 0) 3rem);
    --gradient-topmenu-mobile:
     linear-gradient(to bottom,
     rgba(var(--swatch-topmenu-border-color), 0) 0,
     rgba(var(--swatch-topmenu-bg-color), 1) 0.125rem,
     rgba(var(--swatch-topmenu-bg-color), 1) -moz-calc(3rem - 0.125rem),
     rgba(var(--swatch-topmenu-bg-color), 1) -moz-calc(3rem - 0.125rem),
     rgba(var(--swatch-topmenu-border-color), 0) 3rem);
    --gradient-topmenu-mobile:
     linear-gradient(to bottom,
     rgba(var(--swatch-topmenu-border-color), 0) 0,
     rgba(var(--swatch-topmenu-bg-color), 1) 0.125rem,
     rgba(var(--swatch-topmenu-bg-color), 1) calc(3rem - 0.125rem),
     rgba(var(--swatch-topmenu-bg-color), 1) calc(3rem - 0.125rem),
     rgba(var(--swatch-topmenu-border-color), 0) 3rem);
    --gradient-sidemenu-header:
     linear-gradient(10deg,
     rgba(var(--dark-accent), 0.85) 0,
     rgba(var(--dark-accent), 0.95) 100%);
    --diagonal-stripes: unset;
    --gradient-background: rgba(var(--background-gradient-color));
}

div.hansarp_doc {
    max-width: 99vw !important;
    width: -webkit-calc(var(--body-width-on-desktop));
    width: -moz-calc(var(--body-width-on-desktop));
    width: calc(var(--body-width-on-desktop));
    height: 82vh;
    overflow: hidden !important;
    position: relative;
    max-height: -webkit-calc(var(--body-width-on-desktop) - 7em);
    max-height: -moz-calc(var(--body-width-on-desktop) - 7em);
    max-height: calc(var(--body-width-on-desktop) - 7em);
    background-color: rgb(var(--gray-monochrome));
    border: 1rem double rgb(var(--white-monochrome));
}

div.hansarp_drop {
    -webkit-filter: drop-shadow(-1px 6px 3px rgba(50, 50, 0, 0.5));
     filter: drop-shadow(-1px 6px 3px rgba(50, 50, 0, 0.5));
    z-index: 0;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
}

div.hansarp_container {
    position: absolute;
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
