[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Oxanium&display=swap');
@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Mono:wght@600&display=swap');
@import url('https://fonts.googleapis.com/cssjune?family=Anton');
@import url('https://fonts.googleapis.com/css2?family=Staatliches&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Share+Tech+Mono&display=swap');

:root {
    --header-subtitle: "21 минута до Полуночи";
}

.collapsible-block {
    display: block;
    text-align: center;
    padding: 0.75em;
    background-color: #cacaca45;
    border-top: 0.125rem solid rgb(var(--bright-accent));
    border-bottom: 0.125rem solid rgb(var(--bright-accent));
}

.collapsible-block .collapsible-block-content {
    text-align: left;
}

.goc {
    background: #fff url('https://scp-wiki.wdfiles.com/local--files/theme%3Agoc/goc-fade.png') -5rem -5rem no-repeat!important;
    border-left-color: #275495!important;
    background-size: 30rem;
}
.goc hr {
    background: #275495!important;
    border-color: #fff!important;
}
.goc .scp-image-block{
    background-color: #275495;
    box-shadow: none;
}
div.parapost {
    background:  black;
    border-width: 2px;
    border-style: solid;
    border-color: #1a1a1a;
    padding: 0 1em;
    margin: 1em 3em;
    font-family: var(--mono-font);
}

div.parapost hr {
    border-top: 0.15rem dashed white !important;
    background: transparent !important;
    border-left: none !important;
    border-right: none !important;
}

@media (max-width: 479px) {
   div.parapost {margin: 1em 0;}
}

@media (min-width: 480px) and (max-width: 580px) {
   div.parapost {margin: 0.5em;}
}

.revs {
     border: solid 3px lightgray;
     background: rgb(var(--dark-gray-monochrome));
     padding:0.5rem 1rem;
     font-weight: bold;
}

.revs a:visited, .revs a {
     color:red;
     filter: drop-shadow(0px 0px 0.05rem red);
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}

.addendum {
   border: none;
   border-left: 6px solid rgb(var(--bright-accent));
}

.addendum hr {
   border-color: #f5f5f5;
}

div.banner {
   display: grid;
   grid-template-columns: repeat(2, 50fr);
   grid-template-rows: auto;
   grid-gap: 1rem;
   grid-template-areas:
     "head head"
     "info image";
}

div.bannerhead {
    grid-area: head;
    background: rgb(var(--bright-accent));
    color: white;
    font-family: 'Staatliches', cursive;
    font-size: 5em;
    text-align: center;
    padding: 0;
    position: relative;
    z-index: 1;
    border-left: 12px double #fcfcfc;
    box-shadow: 5px 5px rgba(1,1,1,.25);
    line-height: 1.2;
}

div.bannerinfo{
    grid-area: info;
    background: rgb(var(--bright-accent));
    color: white;
    font-family: 'Share Tech Mono', monospace;
    padding: 0.75rem;
    font-size: 1.5em;
    position: relative;
    z-index: 1;
    border-left: 12px double #fcfcfc;
    box-shadow: 5px 5px rgba(1,1,1,.25);
}

div.bannerinfo hr {
    border-top: 0.15rem dashed white !important;
    background: transparent !important;
    border-left: none !important;
    border-right: none !important;
}
@media (max-width: 571px) {
div.bannerimage img {
height: 150px;
width: 150px;
}
}

div.bannerimage{
    grid-area: image;
    height: min-content;
    position: relative;
    z-index: 0;
}

div.bannerimage img {
  z-index: 0;
  animation: spin 15s cubic-bezier(1,1,1,1) infinite;
filter: drop-shadow(0 0 0.15rem black);
}

.addendum hr {
border-color: #eaeaea!important;
}


@keyframes spin {
   100% { transform: rotate(360deg); }
}

@media only screen and (max-width: 520px) {
div.banner {
   display: grid;
   grid-template-columns: 1fr;
   grid-template-rows: 20fr auto;
   grid-template-areas:
     "head"
     "info"
     "image";
}
div.bannerinfo, div.bannerhead {
    opacity: 0.9;
}
div.bannerimage {
    position: absolute;
    left: 0;
    right: 0;
}
[[/module]]
