[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Anton');
@import url('https://fonts.googleapis.com/css2?family=Staatliches&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Share+Tech+Mono&display=swap');

:root {
    --header-subtitle: "21 минута до Полуночи";
}

.collapsible-block {
    display: block;
    text-align: center;
    padding: 0.75em;
    background-color: #2626264d;
    border-top: 0.125rem solid rgb(var(--bright-accent));
    border-bottom: 0.125rem solid rgb(var(--bright-accent));
}

.collapsible-block .collapsible-block-content {
    text-align: left;
}


.secret, .secret a, .secret a:visited {
    color: transparent;
}

.secret a:hover {
    color: goldenrod;
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}

div.objclass::before {
    background-color: rgb(var(--bright-accent)) !important;
}
div.obj.apollyon {
    background-color: rgb(var(--dark-accent)) !important;
}

div.banner {
   display: grid;
   grid-template-columns: repeat(2, 50fr);
   grid-template-rows: auto;
   grid-gap: 1rem;
   grid-template-areas:
     "head head"
     "info image";
}

div.bannerhead {
    grid-area: head;
    background: rgb(var(--bright-accent));
    color: white;
    font-family: 'Staatliches', cursive;
    font-size: 5em;
    text-align: center;
    padding: 0;
    position: relative;
    z-index: 1;
    border-left: 12px double rgb(var(--swatch-background));
    box-shadow: 5px 5px rgba(1,1,1,.25);
    line-height: 1.2;
}

div.bannerinfo{
    grid-area: info;
    background: rgb(var(--bright-accent));
    color: white;
    font-family: 'Share Tech Mono', monospace;
    padding: 0.75rem;
    font-size: 1.5em;
    position: relative;
    z-index: 1;
    border-left: 12px double rgb(var(--swatch-background));
    box-shadow: 5px 5px rgba(1,1,1,.25);
}

div.bannerinfo hr {
    border-top: 0.15rem dashed white !important;
    background: transparent !important;
    border-left: none !important;
    border-right: none !important;
}

div.bannerimage{
    grid-area: image;
    height: min-content;
    position: relative;
    z-index: 0;
}

div.bannerimage img {
  z-index: 0;
  animation: spin 15s cubic-bezier(1,1,1,1) infinite;
  filter: drop-shadow(0 0 0.15rem red);
}

@keyframes spin {
   100% { transform: rotate(360deg); }
}

@media (max-width: 571px) {
div.bannerimage img {
height: 150px;
width: 150px;
}
}

@media only screen and (max-width: 520px) {
div.banner {
   display: grid;
   grid-template-columns: 1fr;
   grid-template-rows: 20fr auto;
   grid-template-areas:
     "head"
     "info"
     "image";
}

div.bannerinfo, div.bannerhead {
    opacity: 0.9;
}

div.bannerimage {
    position: absolute;
    left: 0;
    right: 0;
}

div.bannerimage img {
    filter: drop-shadow(0 0 0.15rem rgb(var(--bright-accent)));
}
div.obj {
background-color: none;
