[[module css]]
@media (max-width: 767px) {
  th {
    word-break: break-word !important;
  }
}
:root {
    --accent: 0, 210, 60 !important;
}

.series-nav {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-content: center;
    align-items: baseline;

    margin: 1rem 0;
    padding: .02rem 1rem;
    background: #445;

    border-top-right-radius: .67rem;
    border-top-left-radius: .67rem;

    font-size: 1.2em;
    font-family: var(--UI-font);
}

.series-nav>* {
    margin: 0 0.3em;
}

.end_card {
    background: linear-gradient(145deg, rgba(var(--accent), 0.5) 0%, #445 80%);
    padding: 1.25rem 2rem;
    text-align: center;
}
.acs-extra-1 {
background-image: url("https://scpfoundation.net/local--files/component:anomaly-class-bar-source/pending-icon.svg");
background-repeat: no-repeat;
background-position: right;
background-size: 2%;
}
[[/module]]

