[[module CSS]]
@media only screen and (min-width: 1020px) {
  body {
    background: linear-gradient(90deg, rgba(0,0,0,0) 0%, rgb(var(--basalt-primary-color)) 15%, rgb(var(--basalt-primary-color)) 50%, rgb(var(--basalt-primary-color)) 85%, rgba(0,0,0,0) 100%), url(https://scpfoundation.net/local--files/scp-7528/Static.jpg);
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-position: 100% 50%;
  }
}

body {
  isolation: isolate;
}

#header {
  border-bottom: 6px var(--basalt-primary-color)) solid;
  box-shadow: 0px 1.3px 1.5px rgba(0, 0, 0, 0.2), 0px 4.2px 4.9px rgba(0, 0, 0, 0.3);
  background-image: radial-gradient(circle, rgba(0, 0, 0, 0) 0%, rgba(var(--basalt-primary-color),1) 100%);
  background-size: cover;
  background-repeat: no-repeat;
  background-position: 100% 50%;
}

#page-content .widetable :is(table:not(.form), table.wiki-content-table) {
  width: 100% !important;
  margin-top: 1.2rem;
  margin-bottom: 1rem;
}

:is(#page-content table:not(.form), table.wiki-content-table) th :is(div.blockquote, .blockquote, blockquote) {
  font-family: var(--UI-font);
  font-weight: normal;
  font-size: 0.96em;
  text-align: left;
  letter-spacing: 0.033rem;
}

:is(span.bigtext, span.bt) {
  text-transform: uppercase;
}

#page-content .bt {
  color: #f2a;
}

#page-content .yes {
  color: transparent;
  text-shadow: none;
}

#page-content .tert {
  background-color: rgb(var(--basalt-tertiary-color));
}

hr {
  height: 0.22rem;
  transform: skew(-54deg, 0deg);
  background-color: rgb(144, 144, 144);
}

:is(div.notation, div.blockquote, .blockquote, blockquote, div.modal, div.smallmodal, div.jotting, div.papernote, div.document, div.darkdocument) hr {
  background-color: rgb(88, 88, 88);
}

#u-blink-bar p:last-child::after {
  content: "";
  border-right: 1px solid #fff;
  animation: blink 1.0s infinite;
  position: relative;
  left: 1px;
}

@keyframes blink {
  to {
    opacity: .0;
  }
}
