[[module CSS]]
.scp-front-block {
    margin: 16px 0;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    position: relative;
    overflow: hidden;
}

.scp-front-block>* {
    position: relative;
    z-index: 1;
}

.scp-front-block .scp-front-block-bg {
    position: absolute;
    right: -90px;
    bottom: 10px;
    opacity: 5%;
    z-index: 0;
    width: 221px;
}

.scp-front-block .scp-front-block-bg.scp-trans-block-bg {
    right: 0;
    bottom: 10px;
    max-height: calc(100% - 48px);
}

.scp-front-largeblock {
    display: grid;
    grid-template-columns: min-content 1fr;
    grid-template-rows: min-content min-content;
    grid-auto-flow: column;
}

.scp-front-title {
    padding: 4px 24px;
    text-transform: uppercase;
    background: linear-gradient(to top, #d8d8d8, #ececec);
    border-bottom: 1px solid #dcdcdc;
}

.scp-front-featured {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 16px;
}

.scp-front-featured>* {
    margin: 0;
}

.fb-featured {
    margin: 16px 24px;
    line-height: 20px;
}

.fb-featured-nomargin {
    margin: 0;
}

.fb-featured-even {
    display: flex;
    flex-direction: column;
    height: calc(100% - 24px);
    overflow: hidden;
    justify-content: space-between;
}

.fb-featured-even > * {
    flex-grow: 1;
}

.fb-featured-even .fb-new-translation {
    display: flex;
    overflow: hidden;
    min-width: 0;
    align-items: center;
    justify-content: flex-start;
}

.fb-featured-title {
    text-transform: uppercase;
    font-weight: 600;
    margin-bottom: 8px;
}

.fb-featured-title::after {
    content: ':';
}

.fb-featured-title:not(:first-of-type) {
    margin-top: 24px;
}

@media (max-width: 979px) {
    .scp-front-featured {
        grid-template-columns: 1fr;
    }

    .scp-front-largeblock {
        grid-auto-flow: row;
        grid-template-columns: 1fr;
    }

}

.scp-front-buttons {
    display: flex;
    flex-wrap: wrap;
    margin: 16px -6px;
    width: calc(100% + 12px);
    max-width: none !important;
}

.scp-front-button,
.scp-front-button:visited,
.scp-front-button:link {
    display: block;
    margin: 6px;
    border: 1px solid #ccc;
    border-radius: 8px;
    /*flex-basis: calc(33.333333% - 31px);*/
    flex-grow: 1;
    display: flex;
    align-items: center;
    padding: 8px;
    background: linear-gradient(to top, #eee 0%, #eee 80%, #fff 100%);
    text-decoration: none;
    color: #666;
    box-shadow: 0 2px 3px #ccc;
    min-width: 260px;
    white-space: nowrap;
}

.scp-front-button:hover {
    background: linear-gradient(to top, #732717 0%, #732717 80%, #cb4536 100%);
    border: 1px solid #621b0d;
    box-shadow: 0 2px 3px #777;
    color: white;
}

.scp-front-button img {
    height: 64px;
    opacity: 50%;
}

.scp-front-button:hover img {
    filter: invert(1);
    opacity: 100%;
}

.scp-front-button span {
    margin-left: auto;
    margin-right: auto;
    font-size: 24px;
    text-transform: uppercase;
}
[[/module]]
