.experiment h1 {
    text-align: center;
    color: #333;
}

.experiment .title {
    font-size: 140%;
    font-family: 'IBM Plex Serif', serif;
    color: #ffffff;
    background-color: #333333;
    font-weight: 100;
}

.white-wrapper {
    pointer-events:none;  
    position: absolute;
    display: block;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

.white {
    position: absolute;
    display: block;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: #fff;
    opacity: 1;
    animation: warning1 3s;
    animation-delay: 2s;
    animation-fill-mode: forwards;
    z-index: 5;
}

.white-wrapper h1 {
    color: rgba(153, 0, 17, 0.6);
    font-family: Courier New;
    text-align: center;
    position: relative;
    top: 10%;
    left: 0;
    right: 0;
    z-index: 6;
}

.white-wrapper h1 span {
    display: inline-block;
    white-space: nowrap;
    overflow: hidden;
    width: 0;
    animation: warning2 3s steps(40, end);
    animation-delay: 2s;
    animation-fill-mode: forwards;
}

.experiment a {
    animation: warning3 3s;
    animation-delay: 2s;
    animation-fill-mode: forwards;
    z-index: 900;
}

.experiment blockquote a,
.experiment blockquote a:visited {
    color: rgba(200, 220, 190, 0.8);
}

.white-wrapper h1,
.white-wrapper .white {
    pointer-events: none;
}

@keyframes warning1 {
    85% {
    	opacity: 1;
    }
    100% {
    	opacity: 0;
    }
}

@keyframes warning2 {
    50% {
    	width: 100%;
    }
    85% {
    	width: 100%;
    	visibility: hidden;
    }
    100% {
    	width: 100%;
    	visibility: hidden;
    }
}

@keyframes warning3 {
    85% {
    	opacity: 0;
    }
    100% {
    	opacity: 1;
    }
}
