[[module CSS]]
/* щобы выровнять таблицы */

.grn-ylw table tr:nth-child(-n+11) {
  background: #94f194;
}
.grn-ylw table tr:nth-child(-n+21):nth-child(n + 12) {
  background: #ffff89;
}

.author-tables table {
  width: 100%;
}

.author-tables table tr td:nth-child(2) {
  width: 150px;
  text-align: center;
}

.author-tables table tr td:nth-child(3) {
  width: 200px;
  text-align: left;
}

.author-tables table tr td:nth-child(4) {
  width: 120px;
  text-align: center;
}

@media(max-width: 799px) {
  .author-tables table tr td:nth-child(2),
  .author-tables table tr td:nth-child(3),
  .author-tables table tr td:nth-child(4) {
    width: auto;
  }
  #page-content div, #page-content div table {
    font-size: 90%;
  }
}

.scp-front-block {
    margin: 16px 0;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    position: relative;
    overflow: hidden;
}

.scp-front-block>* {
    position: relative;
    z-index: 1;
}

.scp-front-block .scp-front-block-bg {
    position: absolute;
    right: -90px;
    bottom: 10px;
    opacity: 5%;
    z-index: 0;
    width: 221px;
}

.scp-front-block .scp-front-block-bg.scp-trans-block-bg {
    right: 0;
    bottom: 10px;
    max-height: calc(100% - 48px);
}

.fb-featured {
    margin: 16px 24px;
    line-height: 20px;
}
