[[module CSS]]
/* щобы выровнять таблицы */

.grn-ylw table tr:nth-child(-n+11) {
  background: #94f194;
}
.grn-ylw table tr:nth-child(-n+21):nth-child(n + 12) {
  background: #ffff89;
}

.author-tables table {
  width: 100%;
}

.author-tables table tr td:nth-child(2) {
  width: 150px;
  text-align: center;
}

.author-tables table tr td:nth-child(3) {
  width: 200px;
  text-align: left;
}

.author-tables table tr td:nth-child(4) {
  width: 120px;
  text-align: center;
}

@media(max-width: 799px) {
  .author-tables table tr td:nth-child(2),
  .author-tables table tr td:nth-child(3),
  .author-tables table tr td:nth-child(4) {
    width: auto;
  }
  #page-content div, #page-content div table {
    font-size: 90%;
  }
}
[[/module]]
