[[module CSS]]
.classified-banner {
    margin: 16px 0;
    padding: 16px;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    transition: all 0.3s ease;
    position: relative;
    background: #fff url('https://scpfoundation.net/local--files/main/scp_trans.png') center center no-repeat;
}

.classified-banner a, .classified-banner a:link, .classified-banner a:hover, .classified-banner a:visited {
    text-decoration: none;
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    opacity: 0;
}

.classified-banner:hover {
    border: #505052 solid 1px;
    box-shadow: 0 2px 3px #ccc;
}

.c_international-body {
    display: flex;
    flex-wrap: wrap;
}

.c_international-box {
    font-size: 96.5%;
}

.c_international-box {
    text-align: center;
    flex-basis: 9.5rem;
    flex-grow: 1;
}

.c_international-box a img {
    width: 6.75rem;
}

.c_international-text p {
    margin: 0 0 1.25em;
}

@media (max-width: 580px) {
    .c_international-box {
        flex-basis: 6.5rem;
    }

    .c_international-box a img {
        width: 4.5rem;
    }
}

#page-title {
    display: none;
}

.classified-banner {
    text-align: center;
    color: #600;
    line-height: 1.075;
    font-size: 0.975em;
    line-height: 1.6;
}

.hr-main {
    margin: 16px 0;
    padding: 0;
    height: 3px;
    border: none;
    background-color: #707070;
    box-shadow: 0 2px 3px #ccc;
}

.scp-front-block {
    margin: 16px 0;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    position: relative;
    overflow: hidden;
}

.scp-front-block>* {
    position: relative;
    z-index: 1;
}

.scp-front-block .scp-front-block-bg {
    position: absolute;
    right: -90px;
    bottom: 10px;
    opacity: 5%;
    z-index: 0;
    width: 221px;
}

.scp-front-block .scp-front-block-bg.scp-trans-block-bg {
    right: 0;
    bottom: 10px;
    max-height: calc(100% - 48px);
}

.scp-front-largeblock {
    display: grid;
    grid-template-columns: min-content 1fr;
    grid-template-rows: min-content min-content;
    grid-auto-flow: column;
}

.lb-front-title {
	grid-column: 1 / span 3;
}

.lb-single-article {
    min-width: 230px;
    margin: 16px 0;
    padding: 16px 24px;
    font-size: 18px;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.scp-front-simpleblock {
    display: flex;
    flex-direction: column;
}

.scp-front-simpleblock > .lb-title {
    margin-right: auto;
}

.lb-full-block {
    grid-column: 1 / span 2;
    padding: 24px;
}

.lb-title {
    border-bottom: 2px solid #6f6f6f;
    background: #cccccc;
    padding: 4px 24px;
    text-transform: uppercase;
    position: relative;
    display: flex;
    align-items: center;
    min-width: 230px;
}

.lb-title::before,
.lb-title::after {
    position: absolute;
    content: '';
    left: 100%;
    top: 0;
    bottom: -2px;
    width: 24px;
}

.lb-title::before {
    background: #cccccc;
    clip-path: polygon(0 0, calc(100% - 1px) 0, 0 calc(100% - 1px));
}

.lb-title::after {
    background: #6f6f6f;
    clip-path: polygon(0 calc(100% - 2px), calc(100% - 2.5px) 0, 100% 0, 1px 100%, 0 100%);
}

.lb-subtitle {
    margin-left: 24px;
    padding: 4px 10px;
    text-transform: uppercase;
    display: flex;
    align-items: center;
}

.lb-right-block {
    border-left: 1px solid #dcdcdc;
    margin: 16px 0;
    padding: 16px 32px;
}

.lb-many-articles {
    font-size: 14px;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.lb-many-articles a {
    display: block;
    margin: 4px 0;
}

.lb-article-desc {
    display: flex;
    align-items: center;
    flex-direction: row-reverse;
}

.lb-article-class {
    background: #31869b;
    min-width: 80px;
    min-height: 80px;
    margin-left: 32px;
    align-self: center;
    color: white;
    display: flex;
    font-size: 48px;
    line-height: 48px;
    align-items: center;
    justify-content: center;
}

.lb-article-class.lb-safe {
    background: #4e6c77;
    --text: 'S';
}

.lb-article-class.lb-euclid {
    background: #ffc000;
    --text: 'E';
}

.lb-article-class.lb-keter {
    background: #b80000;
    --text: 'K';
}

.lb-article-class.lb-thaumiel {
    background: #7c35b1;
    --text: 'T';
}

.lb-article-class.lb-neutralized {
    background: #dddddd;
    --text: 'N';
    color: black;
}

.lb-article-class.lb-non-standard {
    background: #4479f7;
    --text: '?';
}

.lb-article-class::before {
    content: var(--text);
}

.fb-new-translations {
    display: grid;
    grid-auto-flow: column;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr 1fr;
    margin: 16px 24px;
}

.fb-new-translations p {
    margin: 0;
}

.fb-new-translation {
    padding: 8px 24px;
    border-bottom: 1px solid #dcdcdc;
}

.fb-new-translation:last-of-type {
    border-bottom: none;
}

.scp-front-title {
    padding: 4px 24px;
    text-transform: uppercase;
    background: linear-gradient(to top, #d8d8d8, #ececec);
    border-bottom: 1px solid #dcdcdc;
}

.scp-front-featured {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 16px;
}

.scp-front-featured>* {
    margin: 0;
}

.fb-featured {
    margin: 16px 24px;
    line-height: 20px;
}

.fb-featured-nomargin {
    margin: 0;
}

.fb-featured-even {
    display: flex;
    flex-direction: column;
    height: calc(100% - 24px);
    overflow: hidden;
    justify-content: space-between;
}

.fb-featured-even > * {
    flex-grow: 1;
}

.fb-featured-even .fb-new-translation {
    display: flex;
    overflow: hidden;
    min-width: 0;
    align-items: center;
    justify-content: flex-start;
}

.fb-featured-title {
    text-transform: uppercase;
    font-weight: 600;
    margin-bottom: 8px;
}

.fb-featured-title::after {
    content: ':';
}

.fb-featured-title:not(:first-of-type) {
    margin-top: 24px;
}

.scp-today-block {
    display: flex;
    flex-direction: row;
    overflow: hidden;
    text-overflow: ellipsis;
    word-break: break-word;
    white-space: nowrap;
    height: 55px;
}

.scp-today-block > * {
    min-width: 0;
}

.tb-title {
    padding: 8px 24px;
    position: relative;
    display: flex;
    align-items: center;
    width: calc(230px);
    flex-shrink: 0;
    flex-grow: 0;
    font-size: 18px;
}

.tb-single-article {
    margin: 0;
    padding: 16px 24px;
    font-size: 18px;
    text-align: left;
    flex-grow: 1;
    flex-shrink: 1;
    text-overflow: ellipsis;
    overflow: hidden;
}

.lb-cta-buttons {
    display: flex;
    flex-wrap: wrap;
    padding-top: 0;
    gap: 14px;
}

.lb-buttons-icon-1::before {
  content: "\f004";
}

.lb-buttons-icon-2::before {
  content: "\f061";
}

.lb-buttons-icon-1::before, .lb-buttons-icon-2::before {
  font-family: "Font Awesome 5 Free";
  font-weight: 900;
  position: absolute;
  right: 15px;
  top: 15px;
  font-size: 26px;
}

.d-flex-buttons {
display: flex; 
gap: 14px;
}

.lb-cta-buttons br {
     display: none;
}

.lb-cta-buttons .scp-front-button,
.lb-cta-buttons .scp-front-button:link, 
.lb-cta-buttons .scp-front-button:visited {
     flex: 1 1 0;
     text-align: left;
     margin: 0;
     text-transform: uppercase;
     font-weight: 500;
     font-size: 16px;
     padding: 12px;
     padding-left: 24px;
     align-items: center;
     position: relative;
}

@media (max-width: 979px) {
.d-flex-buttons {
flex-direction: column-reverse;
}
.lb-buttons-icon-1::before, .lb-buttons-icon-2::before {
  top: unset;
}
    .fb-new-translations {
