[[div class="author-tables"]]
[[module ListPages category="., wl artwork" order="created_at desc" separate="yes" perPage="30" tags="-редирект" prependLine="||~ Страница ||~ Рейтинг ||~ Создатель ||~ Дата создания||~ Обновлено||" separate="false"]]
|| %%title_linked%% || %%rating%% (%%rating_votes%%) ||[[user %%created_by%%]] || %%created_at|%H:%M %d.%m.%Y %% || %%updated_at|%H:%M %d.%m.%Y %% ||
[[/module]]
[[/div]]

[[module CSS]]
/* щобы выровнять таблицы */

.author-tables table {
  width: 100%;
}

.author-tables table tr td:nth-child(2) {
  width: 50px;
  text-align: center;
}

.author-tables table tr td:nth-child(3) {
  width: 200px;
}

.author-tables table tr td:nth-child(4) {
  width: 120px;
  text-align: center;
}

@media(max-width: 799px) {
  .author-tables table tr td:nth-child(2),
  .author-tables table tr td:nth-child(3),
  .author-tables table tr td:nth-child(4) {
    width: auto;
  }
  #page-content div, #page-content div table {
    font-size: 90%;
  }
}
[[/module]]
