[[module CSS]]
.create-list {
   display: flex; 
   justify-content: space-between; 
   gap: 16px;
}
.create-list div {
   width: 90%;
   margin: 0 auto;
}
.sub-heading {
   color: #901;
   font-size: 170%;
   font-weight: 600;
   text-align: center;
}
.new-page-form {
  margin: 16px 0;
  border: 1px solid #dcdcdc;
  background: white;
  box-shadow: 0 2px 3px #ccc;
  position: relative;
  overflow: hidden;
}
.new-page-form input[type="text"] {
  height: 25px;
  width: 75%;
  border-radius: 4px;
  border: 1px solid #d1cece;
  padding-left: 8px;
}
.new-page-form input[type="text"]:focus-visible {
  outline: 2px solid #901;
  outline-offset: 1px;
}
.new-page-form input[type="submit"] {
  display: block;
  margin: 6px auto;
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 8px;
  background: linear-gradient(to top, #eee 0%, #eee 80%, #fff 100%);
  color: #666;
  box-shadow: 0 2px 3px #ccc;
  width: 45%;
  text-align: center;
}
.new-page-form input[type="submit"]:hover {
  background: linear-gradient(to top, #732717 0%, #732717 80%, #cb4536 100%);
  border: 1px solid #621b0d;
  box-shadow: 0 2px 3px #777;
  color: white;
  cursor: pointer;
}
.description {
  font-size: 11px;
  text-align: center;
}
@media (max-width: 979px) {
.create-list {
  flex-direction: column;
}
}
.marker-red {
    background: #901;
    color: white;
    border-radius: 50%;
    width: 25px;
    height: 25px;
    justify-content: center;
    align-items: center;
    display: flex;
    margin-right: 10px;
}

.rule-content {
    padding: 0px 0px 0px 35px;
    ;
}

.rule-heading {
    display: flex;
    align-items: center;
    font-weight: 600;
    font-size: 125%;
}

.rule-heading p {
    margin: 0;
    max-width: 85%;
}
[[/module]]
