.lb-title {
    border-bottom: 2px solid #6f6f6f;
    background: #cccccc;
    padding: 4px 24px;
    text-transform: uppercase;
    position: relative;
    display: flex;
    align-items: center;
    width: 25%;
}

.lb-title p {
    margin: 0;
}

.lb-title::before,
.lb-title::after {
    position: absolute;
    content: '';
    left: 100%;
    top: 0;
    bottom: -2px;
    width: 24px;
}

.lb-title::before {
    background: #cccccc;
    clip-path: polygon(0 0, calc(100% - 1px) 0, 0 calc(100% - 1px));
}

.lb-title::after {
    background: #6f6f6f;
    clip-path: polygon(0 calc(100% - 2px), calc(100% - 2.5px) 0, 100% 0, 1px 100%, 0 100%);
}



