.main{
    text-decoration: none!important;
}

.main:hover {
    text-decoration: none!important;
}
.main:visited {     
    color: white;
}

.name {
    font-family: "Roboto Condensed", sans-serif;
    font-size: 3.5em;
    font-weight: 700;
    line-height: 60px;
}

.tale {
    color: #b0d8f7!important;
    font-family: "Roboto Condensed", sans-serif;
    font-size: 3em;
    font-style: italic;
    font-weight: 700;
}

.subtitle {
    font-family: "Roboto Condensed", sans-serif;
    color: white;
    FONT-SIZE: 1.4em;
    position: relative;
    font-style: italic;
    font-weight: 200;
    top: -0.7em;
}

@media only screen and (max-width: 26.5625rem) {
.name {
    font-family: "Roboto Condensed", sans-serif;
    font-size: 2em;
    font-weight: 700;
    line-height: 35px;
}

.tale {
    color: #b0d8f7!important;
    font-family: "Roboto Condensed", sans-serif;
    font-size: 1em;
    font-style: italic;
    font-weight: 700;
}

.subtitle {
    font-family: "Roboto Condensed", sans-serif;
    color: white;
    FONT-SIZE: 1em;
    position: relative;
    font-style: italic;
    font-weight: 200;
    top: -0.7em;
}
}

.main {
    display: block;
    height: 18em;
    padding-left: 0.75rem;
    color: white;
    border-radius: 3px;
}

.art {
    position: relative;
    background-position: 25% 40%;
    background-size: 130%;
    background-color: #00295D;
    background-blend-mode: luminosity;
    transition: all 200ms ease-in-out;
    color: white! important;

    margin-right: 32em;
    margin-top: 1em;
}

.art:hover {
    color: #b0d8f7!important;
    transition: all 150ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(0,0,0,0.5);
    text-shadow: 1px 1px 0px #051c2e;
    mix-blend-mode: none;
}

.art:hover .subtitle {
    color: white;
}

.art:hover .info {
    color:#557D99!important;
}


.right {
    position: relative;
    background-position: 25% 40%;
    background-size: 140%;
    background-color: #00295D;
    background-blend-mode: luminosity;
    transition: all 200ms ease-in-out;
    color: white! important;
    margin-left: 32em;

    margin-top: -19em;
}

.right:hover {
    color: #b0d8f7!important;
    transition: all 150ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(0,0,0,0.5);
    text-shadow: 1px 1px 0px #051c2e;
    mix-blend-mode: none;
}

.right:hover .subtitle {
    color: white!important;
}

.right:hover .info {
    color:#557D99!important;
}


#page-title{display:none}

.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    height: calc(42.2em);
    max-width: 100dvw !important;
    background-image: url('https://scpfoundation.net/local--files/sandbox:integration-program-hub/new.png');
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top;
    margin-top: -3.70em;
}

@media only screen and (max-width: 26.5625rem) {
.widebox {
    position: relative;
    width: 100dvw !important;
    left: calc(-50dvw + 50%);
