:root {

    --timeScale: 6;
    --timeDelay: 6s;
    --red: 176, 216, 247;
    --bluedark: 5, 28, 46;
    --blue-dark-lighter: 10, 62, 102;
     --medium-accent: var(--red);
     --pale-gray-monochrome: var(--blue-dark-lighter);
     --cutColour: #fafafa;
     --barColour: var(--red);
     --toggle-border-color: rgb(var(--red));
     --toggle-icon-color: rgb(var(--red));
     --mono-font: 'IBM Plex Mono', monospace;

    --header-gradient-color-top: var(--bluedark);
    --header-gradient-color-middle: var(--bluedark);
    --header-gradient-color-bottom: var(--bluedark);
    --background-gradient-distance: 0rem;
    --diagonal-stripes: linear-gradient(transparent 0);

    --swatch-topmenu-bg-color: var(--red);
    --swatch-topmenu-border-color: var(--red);

      --swatch-background: var(--bluedark);

     --dark-accent: var(--red);
     --barColour: var(--red);
     --bright-accent: var(--red);

    --header-title: "ПРОГРАММА ИНТЕГРАЦИИ";
    --header-subtitle: " ";
    --logo-image: url("");

    --ui-button-bg: var(--blue-dark-lighter);
    --ui-button-txt: var(--red);
    --ui-button-hover-txt: var(--bluedark);

    --link-color: none;
    --visited-link-color: none;
    --hover-link-color: none;
    --newpage-color: none;

    --rating-module-bg-color: var(--red);
    --rating-module-bottom-border-color: var(--blue-dark-lighter);

    --rating-module-text-color: var(--bluedark);
    --rating-module-text-hover-color: var(--swatch-text-secondary-color);

--swatch-text-dark: var(--white-monochrome);

--header-font: 'Big Shoulders Text', sans-serif;
--title-font: "Big Shoulders Text", sans-serif;

--header-h1-font-size: calc(1rem + (2.405 - 1.15) * ((var(--header-width-on-mobile, 100vw) - 18.75rem) / (48 - 18.75)));
}

#main-content {
    --ui-icon-color: var(--red);
    --ui-icon-bg: var(--bluedark);
    --ui-icon-hover-color: var(--red);
    --ui-icon-hover-bg: var(--bluedark);

    --editor-icon-color: var(--bluedark);
    --editor-icon-bg: var(--red);
    --editor-icon-hover-color: var(--red);
    --editor-icon-hover-bg: var(--blue-dark-lighter);
    --editor-icon-submenu-bg: var(--blue-dark-lighter);

    --pagetags-title-text: var(--bluedark);

}

#side-bar,
#interwiki {
    --sidebar-bg-color: var(--blue-dark-lighter);
    --sidebar-resources-bg-color: var(--win-border), 0.25;
    --sidebar-media-bg-color: var(--win-border), 0.25;

    --sideblock-heading-border-color: var(--bluedark);
    --sideblock-heading-bg-color: var(--bluedark);
    --sideblock-heading-text-color: var(--red);

    --sidebar-border-color: var(--bluedark), 0.08;
    --sidebar-subtest-color: var(--bluedark);

    --sidebar-links-text: var(--red);
    --sidebar-links-hover-bg-color: var(--red);
    --sidebar-links-hover-text-color: var(--bluedark);
}

#footer {
    --footer-bg-color: var(--bluedark);
    --footer-text-color: var(--red);
    --footer-link-color: var(--red);
    --footer-link-hover-color: 176, 216, 247;
    --footer-link-hover-bg-color: var(--blue-dark-lighter);
}

form#edit-page-form .wd-editor-toolbar-panel {
