:root {
  --fg-lighter: #ce3ebd;
  --fg-light: #b998e0;
  --fg-medium: #613c8b;
  --fg-dark: #160e20;
}

/* Grid Layout */
@supports(display: grid) {
#container {
  display: grid;
  min-height: 100vh;
  grid-template-areas:
    "header"
    "content-wrap"
    "footer"
    "license";
  grid-template-rows: auto 1fr auto auto;
}
#header {
  grid-area: header;
}
#content-wrap {
  grid-area: content-wrap;
  /* min-height: 0; */
  width: 100%;
}
#side-bar {
  max-height: -webkit-calc(100% - 2em);
  max-height: -moz-calc(100% - 2em);
  max-height: calc(100% - 2em);
  margin: -0.5em -0.5em 0;
  padding: 0.5em 0.5em 0;
  /* overflow-y: auto; */
}
@media (max-width: 767px) {
  #side-bar {
    max-height: none;
    margin: 0;
    padding: 1em 1em 0 1em;
  }
}
#page-content {
  /* min-height: 0; */
}
#footer {
  grid-area: footer;
}
#license {
  grid-area: license;
}
}

/* Scroll Bar */
#side-bar,
#page-content > .collapsible-block > div.collapsible-block-unfolded > .collapsible-block-content {
  scrollbar-width: thin;
  scrollbar-color: rgba(170, 170, 170, 0.4) rgba(170, 170, 170, 0.05);
}
