.login {
  position: absolute;
  top: 50vh;
  width: 100%;
}
.subtitle {
  position: relative;
  top: -2.5vh;
  margin-bottom: 1.5rem;
  text-align: center;
  font-size: 2em;
  font-weight: bold;
  color: #eee;
  opacity: 0;
  -webkit-animation: ani-showsubtitle 5s ease-in-out forwards;
  animation: ani-showsubtitle 5s ease-in-out forwards;
}
@keyframes ani-showsubtitle {
  0%, 79% {
    top: -2.5vh;
    opacity: 0;
    -webkit-transform: scaleY(0);
    -moz-transform: scaleY(0);
    -ms-transform: scaleY(0);
    -o-transform: scaleY(0);
    transform: scaleY(0);
  }
  80% {
    top: -2.5vh;
    opacity: 0;
    -webkit-transform: none;
    -moz-transform: none;
    -ms-transform: none;
    -o-transform: none;
    transform: none;
  }
  100% {
    top: 0;
    opacity: 1;
    -webkit-transform: none;
    -moz-transform: none;
    -ms-transform: none;
    -o-transform: none;
    transform: none;
  }
}
.subtitle span {
  display: inline-block;
  overflow: hidden;
}
.subtitle span:nth-child(2) {
  width: 0;
  height: 0;
  opacity: 0;
}
.login-form {
  position: relative;
  top: 5vh;
