source:
[[>]]
[[module Rate]]
[[/>]]

**Объект №:** SCP-0001-RU-S

**Класс объекта:** Устанавливается

**Особые условия содержания:** Будут определены по результатам испытаний
[[html]]
<style>
@import url(https://scpfoundation.net/-/static/scp-base.css);
@import url(https://files.scpfoundation.net/local--files/scp-0001-ru-s/style.css);
@import url(https://files.scpfoundation.net/local--files/scp-0001-ru-s/style2.css);
</style> 

<div class="rimg">
<img id='pic1' src='' alt='' style="width: 300px;" class="image">
<br>
<span id="ApicTxt" style="width: 300px;">A</span>
</div>

<p id="Ofield">Loading...</p>

<script type="text/javascript">

now = new Date();

year0=now.getFullYear();
month0=('0'+(now.getMonth()+1)).slice(-2);
date0=('0'+now.getDate()).slice(-2);

now.setDate(now.getDate()-7);

year=now.getFullYear();
month=('0'+(now.getMonth()+1)).slice(-2);
date=('0'+now.getDate()).slice(-2);
hour = now.getHours();


opt0 =  Math.floor((Math.random() * 10) + 1);
switch (opt0) {
 case 1: 
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p1.jpg";
    opt0 = "Человек, первым обнаруживший SCP-0001-RU-S."; break;
 case 2: 
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p2.jpg";
    opt0 = "Мобильная группа, захватившая SCP-0001-RU-S"; break;
 case 3: 
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p3.jpg";
    opt0 = "Один из гражданских, видевших SCP-0001-RU-S"; break;
 case 4: 
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p4.jpg";
    opt0 = "Место, где расположена лаборатория нестандартных испытаний"; break;
 case 5: 
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p5.jpg";
    opt0 = "Разрушения, причинённые ближайшему городу во время захвата SCP-0001-RU-S"; break;
 case 6:
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p6.jpg";
    opt0 = "Заведующий лабораторией в момент, когда он узнал о существовании SCP-0001-RU-S"; break;
 case 7:
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p7.jpg";
    opt0 = "[ОТРЕДАКТИРОВАНО] пытается взглянуть на SCP-0001-S поближе"; break;
 case 8: 
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p8.jpg";
    opt0 = "Королевский пингвин, абсолютно никак не связанный с SCP-0001-S"; break;
 case 9: 
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p9.jpg";
    opt0 = "Транспорт, доставивший МОГ к месту захвата SCP-0001-S"; break;
 default:
    img0 = "https://files.scpfoundation.net/local--files/scp-0001-ru-s/p10.jpg";
    opt0 = "Неустановленное лицо, подозреваемое в создании SCP-0001-S";
 }

document.getElementById("pic1").src=img0; 
document.getElementById("ApicTxt").innerHTML=opt0;


opt1 =  Math.floor((Math.random() * 10) + 1);
switch (opt1) {
 case 1: 
    opt1 = "кубический"; break;
 case 2: 
    opt1 = "шарообразный"; break;
 case 3: 
    opt1 = "трапециевидный"; break;
 case 4: 
    opt1 = "экстрамерный"; break;
 case 5: 
    opt1 = "звездообразный"; break;
 case 6: 
    opt1 = "вогнуто-выгнутый"; break;
 case 7: 
    opt1 = "пирамидообразный"; break;
 case 8: 
    opt1 = "желеобразный"; break;
 case 9: 
    opt1 = "леденящий душу"; break;
 default:
    opt1 = "конусообразный, с множеством сквозных отверстий";
 }

opt2 =  Math.floor((Math.random() * 10) + 1);
switch (opt2) {
 case 1: 
    opt2 = " шлем виртуальной реальности "; break;
 case 2: 
    opt2 = " монокль с половинкой уса "; break;
 case 3: 
    opt2 = " котелок "; break;
 case 4: 
    opt2 = " цилиндр "; break;
 case 5: 
    opt2 = " слуховой аппарат "; break;
 case 6: 
    opt2 = " зубной протез на Хэллоуин, прикрепляемый поверх губ, "; break;
 case 7: 
    opt2 = " козырёк, который носили бухгалтеры в Америке 30-х годов, "; break;
 case 8: 
    opt2 = " лопнувший футбольный мяч, со следами ношения на чьей-то макушке, "; break;
 case 9: 
    opt2 = " шарик для пинг-понга, разломанный пополам и соединённый медной проволокой наподобие очков, "; break;
 default:
    opt2 = " судейский парик из меха ондатры ";
 }

opt3 =  Math.floor((Math.random() * 10) + 1);
switch (opt3) {
 case 1: 
    opt3 = "красного"; break;
 case 2: 
    opt3 = "лазурного с золотыми нотками"; break;
 case 3: 
    opt3 = "фиолетово-чёрного"; break;
 case 4: 
    opt3 = "изумрудно-зелёного в малиновую крапинку"; break;
 case 5: 
    opt3 = "асфальтового с бледно-голубыми полосками"; break;
 case 6: 
    opt3 = "физически невозможного, но тем не менее существующего"; break;
 case 7: 
    opt3 = "бледно-розового с морковным отливом"; break;
 case 8: 
    opt3 = "изысканного кораллового"; break;
 case 9: 
    opt3 = "серо-буро-кремового"; break;
 default:
    opt3 = "изменяющегося в зависимости от освещения и сексуальной ориентации носителя";
 }

opt4 =  Math.floor((Math.random() * 10) + 1);
switch (opt4) {
 case 1: 
    opt4 = "в глубоких катакомбах <a href='scp-110'>подземного города</a>, обвешанным знаками 'Осторожно, технодизайн!',"; break;
 case 2: 
    opt4 = "в куче барахла, ежедневно вынимаемой любопытными охранниками из <a href='scp-1162'>дыры в стене</a>,"; break;
 case 3: 
    opt4 = "на дне <a href='scp-1201'>фонтана Заратустры</a> (носил ли его сам Заратустра, в настоящее время уточняется)"; break;
 case 4: 
    opt4 = "соскользнувшим по <a href='scp-144'>канату в небеса</a> в предсказанный день,"; break;
 case 5: 
    opt4 = "в <a href='scp-1557'>чистилище жирафов</a>, надетым на одну из особей,"; break;
 case 6: 
    opt4 = "в <a href='scp-1663'>зоне содержания 1663-0</a> в плексигласовом контейнере с надписью «Дорогому Механику в день рождения от библиотекарей»"; break;
 case 7: 
    opt4 = "<a href='scp-2088'>подо льдом</a>, который распоясавшийся персонал колол для вечерней порции маргариты,"; break;
 case 8: 
    opt4 = "в <a href='https://scpfoundation.net/scp-416'>бесконечном лесу</a> косматым геологом, рассказавшим заодно, как одинока без мужика тайга, и как она плачет, и как она получит [ДАННЫЕ ПОВРЕЖДЕНЫ]"; break;
 case 9: 
    opt4 = "привязанным на цепь <a href='https://scpfoundation.net/scp-2233-ru-j'>лукоморья</a> (допрос кота завершился неудачей)"; break;
 default:
    opt4 = "в сумке сторожа <a href='https://scpfoundation.net/scp-9999-ru-j'>музея славы и всякой всячины</a> при попытке выноса с территории";
 }

opt5 =  Math.floor((Math.random() * 10) + 1);
switch (opt5) {
 case 1: 
    opt5 = " МОГ-1 «Глубокогорные таксидермисты» "; break;
 case 2: 
    opt5 = " МОГ-2 «Дозорные скотомогильников» "; break;
 case 3: 
    opt5 = " МОГ-3 «Летучие страусы» "; break;
 case 4: 
    opt5 = " МОГ-4 «Жгучие тушканчики» "; break;
 case 5: 
    opt5 = " МОГ-5 «Дегустаторы плесени» "; break;
 case 6: 
    opt5 = " МОГ-6 «Квадратно-гнездовые ударники» "; break;
 case 7: 
    opt5 = " МОГ-7 «Мушиные бляшки» "; break;
 case 8: 
    opt5 = " МОГ-8 «Басовые ключи» "; break;
 case 9: 
    opt5 = " МОГ-9 «Кондратии Кондраки и Компания» "; break;
 default:
    opt5 = " МОГ-10 «Свидетели Кроули» ";
 }

opt6 =  Math.floor((Math.random() * 10) + 1);
switch (opt6) {
 case 1: 
    opt6 = "на склад Зоны 666 в хранилище для нечистых предметов"; break;
 case 2: 
    opt6 = "на ковёр доктору Альто Клефу, дабы продемонстрировать, на что были потрачены шесть миллионов"; break;
 case 3: 
    opt6 = "в женскую уборную Зоны 19, чтобы поглумиться над новыми лаборантками"; break;
 case 4: 
    opt6 = "в спальню доктора Кроу и спрятан под кроватью"; break;
 case 5: 
    opt6 = "на кухню Зоны 7██ Российского Филиала в качестве нового элемента униформы для поваров"; break;
 case 6: 
    opt6 = "прямиком в мусоросжигатель, который, к сожалению, в тот день был неисправен"; break;
 case 7: 
    opt6 = "в гараж командира МОГ, а затем перепродан [УДАЛЕНО], который и принёс объект в Участок"; break;
 case 8: 
    opt6 = "в камеру содержания <a href='https://scpfoundation.net/scp-682'>одного безобидного существа</a> из-за ошибки в накладной"; break;
 case 9: 
    opt6 = "в Зону 7██ силами Почты России, перевозившей его несчастные █ км почти 6 суток (сотрудник, воспользовавшийся услугами ПР уже <a href='https://scpfoundation.net/scp-173'>моет полы</a> в одном слегка загрязнившемся контейнере)"; break;
 default:
    opt6 = "в Зону 19, откуда выслан обратно в Россию с пометкой [НЕПЕРЕВОДИМАЯ ИГРА СЛОВ УДАЛЕНА]";
 }

opt7 =  Math.floor((Math.random() * 10) + 1);
switch (opt7) {
 case 1: 
    opt7 = "разразившейся в Зоне 7██ эпидемии дизентерии"; break;
 case 2: 
    opt7 = "эктоплазменного заражения хламидиальных клеток всех лаборантов Зоны 7██"; break;
 case 3: 
    opt7 = "вконец обленившихся работников Зоны 7██"; break;
 case 4: 
    opt7 = "недавнего нашествия белок-крестоносцев"; break;
 case 5: 
    opt7 = "тестирования на персонале экспериментальной вакцины от косоглазия"; break;
 case 6: 
    opt7 = "паранормального облысения ног всех работников мужского пола Зоны 7██"; break;
 case 7: 
    opt7 = "недавнего неудавшегося опыта по зачислению в штат «абсолютно безопасного свежевыведенного заводчиками Зоны 7██ пит-буль-хаунд-мастиф-двадцать-два-ряда-зубов-терьера»"; break;
 case 8: 
    opt7 = "диверсии с суперклеем на сидушках унитазов в отхожих местах Зоны 7██"; break;
 case 9: 
    opt7 = "неудачного испытания доктором Гирсом системы активации самоуничтожения Зоны 7██ с паролем, который «уж теперь-то ну ни за что нельзя ввести наугад c первой попытки»,"; break;
 default:
    opt7 = "вторжения в Зону 7██ говорящих плотоядных головастиков";
 }

opt8 =  Math.floor((Math.random() * 10) + 1);
switch (opt8) {
 case 1: 
    opt8 = "очистки от кишок и крови"; break;
 case 2: 
    opt8 = "стерилизации в ультрафиолете ламп расположенного неподалёку ночного клуба"; break;
 case 3: 
    opt8 = "пары пинков от командующего группой захвата"; break;
 case 4: 
    opt8 = "аккуратного тычка длинным прутиком, который не повлёк за собой серьёзных последствий"; break;
 case 5: 
    opt8 = "серии полевых испытаний в портативной микроволновке"; break;
 case 6: 
    opt8 = "выяснения того факта, что материал объекта прочнее зубов оголодавшего пса лидера батальона"; break;
 case 7: 
    opt8 = "опустошения в сторону «подозрительно выглядящей хреновины» всех рожков"; break;
 case 8: 
    opt8 = "очистки от многовекового слоя девственной пыли и [ДАННЫЕ УДАЛЕНЫ], предположительно летучих мышей"; break;
 case 9: 
    opt8 = "того, как группа протрезвела от устроенной на радостях пьянки"; break;
 default:
    opt8 = "того, как стало ясно, что в хозяйстве никому из оперативников объект не пригодится";
 }

opt9 =  Math.floor((Math.random() * 10) + 1);
switch (opt9) {
 case 1: 
    opt9 = " после серии подозрительных случаев моментального ожирения перелётных птиц"; break;
 case 2: 
    opt9 = " после перевода анонимного письма, доставленного в гостиничный номер главы Российского филиала с языка индейцев Навахо"; break;
 case 3: 
    opt9 = " после расшифровки 2-й главы рукописи Войнича"; break;
 case 4: 
    opt9 = " после отправки в космос сигнала с трёхэтажным матом пьяным [УДАЛЕНО]"; break;
 case 5: 
    opt9 = " перед почти приведённым в исполнение протоколом устранения Главного исследователя, минусовавшего объекты лолфонда на полигоне"; break;
 case 6: 
    opt9 = " после вдумчивого изучения «Новой Хронологии» доктора Фоменко"; break;
 case 7: 
    opt9 = " после успешного отражения атаки Челябинского метеорита силой мысли доктора [ОТРЕДАКТИРОВАНО]"; break;
 case 8: 
    opt9 = " после обнаружения у нового освежителя воздуха в уборных Фонда галлюциногенных свойств"; break;
 case 9: 
    opt9 = " непосредственно перед тем, как в Зону 19 был подан рапорт о признании Российского филиала недействительным в связи с отсутствием хотя бы одного местного объекта с рейтингом выше, чем у <a href='https://scpfoundation.net/scp-895'>искажений на видео</a>"; break;
 default:
    opt9 = " <a href='https://scpfoundation.net/scp-1092-ru'>после явления как бы случайного доктору знания необычайного</a>";
 }

opt10 =  Math.floor((Math.random() * 10) + 1);
switch (opt10) {
 case 1: 
    opt10 = "ото всех желающих посодействовать науке"; break;
 case 2: 
    opt10 = "ото всех, кто в душе немного чувствует себя подопытным кроликом,"; break;
 case 3: 
    opt10 = "от любого, кто не боится увидеть свою фотографию в симпатичной траурной рамочке в главном холле,"; break;
 case 4: 
    opt10 = "ото всех рисковых везунчиков, которые с лёгкостью потрошат одноруких бандитов в игровых клубах,"; break;
 case 5: 
    opt10 = "ото всех, кто согласен рискнуть за дополнительную дозу [ДАННЫЕ УДАЛЕНЫ РОСКОМНАЗДОРОМ],"; break;
 case 6: 
    opt10 = "от любителей острых ощущений и ударов тупыми предметами по голове"; break;
 case 7: 
    opt10 = "от каждого, кто любит нажимать все встреченные по дороге к рабочему месту большие красные кнопки,"; break;
 case 8: 
    opt10 = "от каждого сорвиголовы, не боящегося сорвать вдобавок к голове ещё и пару-тройку конечностей,"; break;
 case 9: 
    opt10 = "ото всех, кто не боится рвать шаблоны и голосовые связки,"; break;
 default:
    opt10 = "от обладателей пытливых умов, ждущих новых изысканных пыток,";
 }

document.getElementById("Ofield").innerHTML= "<b>Описание:</b> SCP-0001-S представляет собой " + opt1 + opt2 + opt3 + " цвета. О местоположении объекта стало известно " + date + "." + month + "." + year + opt9 + ". В результате обработки накопленных при наблюдении за этим процессом данных объект был найден " + opt4 + " и захвачен  группой " + opt5 + "почти без потерь. После " + opt8 + " SCP-0001-S был доставлен " + opt6 + ". Через сутки, пройдя частичную дезинфекцию, объект попал в лабораторию по исследованию аномальных материалов для полноценной классификации и постановки на содержание." +
"<br><br><br>Внимание! Из-за " + opt7 + " Организация испытывает временные трудности с наличием работоспособных кадров для исследования SCP-0001-S! В течение недели после захвата объекта " + opt10 + " принимаются заявки на предоставление его в персональное пользование для изучения аномальных свойств. В конкурсном отборе может принять участие любой действующий сотрудник Фонда. Срок подачи заявок истекает сегодня (" + date0 + "." + month0 + "." + year0 + ") в районе " + hour + " часов.";
</script>
[[/html]]

[[table style="width: 100%;"]]
[[row]]
[[cell style="padding: 5px; background-color: #FFFFF7; border: 1px solid silver; width: 95%;"]]
[[collapsible show="✉ >>ЗАПОЛНИТЬ ФОРМУ И ОТПРАВИТЬ ЗАЯВКУ ВОЛОНТЁРА<< ✉" hide=" " folded="yes" ]]
[[html]]
<style>
@import url(https://files.scpfoundation.net/local--files/scp-0001-ru-s/style.css);
@import url(https://files.scpfoundation.net/local--files/scp-0001-ru-s/style2.css);
</style>
<div>
<p id="Ofield">Loading...</p>
<script type="text/javascript">

p_count = Math.floor((Math.random() *1000) + 100);

title =  Math.floor((Math.random() *10) + 1);
switch (title) {
 case 1: 
    title = "сотрудников класса D"; break;
 case 2: 
    title = "проштрафившийся персонал, отправлявшийся ранее на кетеры"; break;
 case 3: 
    title = "докторов, прошедших курсы <a href='https://scpfoundation.net/scp-710-jp-j'>самообороны</a>"; break;
 case 4: 
    title = "докторов, НЕ являющихся членами РАЕН"; break;
 case 5: 
    title = "докторов, по чьей вине в прошлом месяце не погибло ни одного сотрудника класса D"; break;
 case 6: 
    title = "докторов, у которых отсутствует аллергия на амнезиаки"; break;
 case 7: 
    title = "младших научных сотрудников без опыта работы, принятых на стажировку в Организацию не более трёх дней назад"; break;
 case 8: 
    title = "сотрудников, имевших до прихода в Организацию опыт устранения лжепророков теории хаоса"; break;
 case 9: 
    title = "сотрудников, над которыми втайне от них проводятся опыты по изучению воздействия [ДАННЫЕ УДАЛЕНЫ]"; break;
 default:
    title = "персонал, который не представляет серьёзную ценность для Фонда";
 }

scpname =  Math.floor((Math.random() *10) + 1);
switch (scpname) {
 case 1: 
    scpname = "<a href='https://scpfoundation.net/scp-062'>квантовым компьютером</a> с ножным электроприводом"; break;
 case 2: 
    scpname = "<a href='https://scpfoundation.net/SCP-915'>механоэкстрамерным компьютером</a> на лунных батарейках"; break;
 case 3: 
    scpname = "<a href='https://scpfoundation.net/SCP-155'>бесконечно быстрым компьютером</a> за бесконечно долгое время"; break;
 case 4: 
    scpname = "калькулятором с <a href='https://scpfoundation.net/SCP-1163-RU'>процессором российского производства</a>"; break;
 case 5: 
