   //resultTxt = resultTxt.substr(1, resultTxt.length - 3);

   resultTxt = (resultTxt + ' ' + theTexts[4][itemAbility][0]).trim() + '</font>»';



   //update limits

   randLims[pressedBtn][1] = randLims[pressedBtn][1] + 2;

   for (i=1; i<5; i++){
      if (i != pressedBtn){
