   if (noDisplay == 1) {

           resultTxt = interfaceText[51];
           noDisplay = 0;
   }

   if (showChars > 0){

        if (foodStats[0] < 0) {foodStats[0] = '<font color = "#ff0000">' + foodStats[0] + '</font>'} else { foodStats[0] = '<font color = "#006600">+' + foodStats[0] + '</font>'}
