    	if (foodStats[2] < 0) {foodStats[2] = '<font color = "#006600">' + foodStats[2] + '</font>'} else { foodStats[2] = '<font color = "#ff0000">+' + foodStats[2] + '</font>'}

        resultTxt = resultTxt + '<br>[' + interfaceText[83].replace(':hp:',foodStats[0]).replace(':hg:',foodStats[1]).replace(':th:',foodStats[2]) + ']';
        showChars = showChars - 1;
   }

 } else {


    resultTxt = resultTxt + interfaceText[24];

 }





//if (step==5) { isDead = 1};



   //Subject is dead or Run away
  if (isDead > 0 || isRunAway > 0) {
