[[module CSS]]

div.terminal{
    border: 1px solid black;
    border: solid 3px #BBBBBB;
    border-radius: 16px;
    background-color: #131;
    /* black shadow at the top of the terminal */
    background-image:
        radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #121);
    background-position: center;
    display: block;
    /* shadow around the terminal */
    box-shadow: inset 0 0 10em 1em rgba(0,0,0,0.5);
    /* prevents scanline from forming a scrollwheel */
    overflow:hidden;
}
div.terminal blockquote {
    background-color: black;
    border: double 3px #80FF80
}
div.scanline{
    margin-top: -40%;
    width: 100%;
    height: 60px;
    position: relative;
    pointer-events: none;
    /* Safari 4.0 - 8.0 */
    -webkit-animation: scan 12s linear 0s infinite; /* You may need to change this. If the bar goes by too quickly, increase 5s. */
    animation: scan 12s linear 0s infinite; /* Same as above */
    background: linear-gradient(to bottom, rgba(56, 112, 82,0), rgba(56, 112, 82,0.1)) !important;
}

div.text{
    color: rgba(128,255,128,0.8);
    padding-left: 2em;
    padding-top: 40%;
    font-family: monospace;
    font-size: 1.2em;
}

/* Safari 4.0 - 8.0 */
@-webkit-keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* You may need to change this depending on the length of your terminal. If the scanline stops halfway, increase the second number.*/
}

@keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* Same as above.*/
}

div.text a {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a.newpage {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a:hover {
    color: #131;
    text-decoration: underline;
    background-color: #80FF80;
    padding: 1px;
}
div.text a:hover::before{
    content: "> ";
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translate(0,30px);
  }
  to {
    opacity: 1;
    transform: translate(0,0);
  }
}

.collapsible-block-unfolded .terminal .block {
  animation-name: fadeIn;
  animation-duration: 1s;
  animation-iteration-count: 1;
  animation-timing-function: ease-out;
  animation-fill-mode: backwards;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(1) {
  animation-delay: 0.5s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(2) {
  animation-delay: 1s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(3) {
  animation-delay: 1.5s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(4) {
  animation-delay: 2s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(5) {
  animation-delay: 2.5s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(6) {
  animation-delay: 3s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(7) {
  animation-delay: 3.5s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(8) {
  animation-delay: 4s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(9) {
  animation-delay: 4.5s;
}

.collapsible-block-unfolded .terminal .block:nth-of-type(10) {
  animation-delay: 5s;
}
[[/module]]

