[[module CSS]]
@import url(http://fonts.googleapis.com/css?family=Cinzel);
@import url(http://fonts.googleapis.com/css?family=La+Belle+Aurore);
@import url(http://fonts.googleapis.com/css?family=Kristi);


body {
    background-color: #ff00ef;
    font-size: 0.9em;
    color: #ffe300;
}


/* Rating module */

.page-rate-widget-box .rate-points {
    background-color: #007eb3 !important;
    border: solid 1px #007eb3;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #333333;
    border-top: solid 1px #007eb3;
    border-bottom: solid 1px #007eb3;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: white;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #707070;
    color: white;
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #333333;
    border: solid 1px #007eb3;
}
.page-rate-widget-box .cancel a {
    color: 007eb3;
}
.page-rate-widget-box .cancel a:hover {
    background: #707070;
    color: white;
}
/* Tabs */

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border: 0;
    background: 0;
    padding: 0;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    color: #ffe300;
    border-width: 0 0 2px;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
  background: #ffe300;
  border: 0;
  color: #ffe300;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
  border: 0;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
  background: #ffe300;
  color: #ffe300;
  font-weight: bold;
}

/* Collapsibles */

#page-content .collapsible-block {
    position: relative;
    padding: 0.5em;
    margin: 0.5em;
    box-shadow: 0 1px 3px rgba(0,0,0,.2);
    overflow-wrap: break-word;
}

#page-content .collapsible-block-unfolded{
    color: black;
    overflow-wrap: break-word;

}

#page-content .collapsible-block-unfolded-link {
    text-align:center;
}

#page-content .collapsible-block-folded {
    text-align: center;
    color: dimgrey;
}

#page-content .collapsible-block-link {
    font-weight: bold;
    color: black;
    text-align: center;
}
.collapsible-block blockquote {
    position: relative;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.42);
    background-color: rgba(0, 0, 0, 0.42);
    border: 1px solid ##ff00ef;
    color: #ffe300;
    margin: 2%;
    font-family: 'Roboto Mono', 'Noto Sans TC', 'Noto Sans JP', 'Noto Sans KR', monospace;
    font-weight: normal !important;
}
.collapsible-block blockquote h3 {
    color: #ff00ef;
    font-family: 'Roboto Mono', 'Noto Sans TC', 'Noto Sans JP', 'Noto Sans KR', monospace;
    font-weight: normal !important;
}

/* RIDIA Theme */

.ridia-sfondo{
    background-color: #f8ecc2;
    color: #ff00ef;
    width: 90%;
    padding-top: 5px;
    padding-right: 30px;
    padding-bottom: 30px;
    padding-left: 30px;
    margin: 0 auto;
}

.ridia-titolo{
    font-family: 'Cinzel';
    font-weight: bold;
    font-size: 26px;
    line-height: 150%;
}

.ridia-documento{
    font-family: 'Cinzel';
    font-weight: bold;
    font-size: 14px;
}

.ridia-testo{
    font-family: 'La Belle Aurore';
    font-weight: normal;
    font-size: 20px;
    line-height: 150%;
}

.ridia-firma{
    font-family: 'Kristi';
    font-size: 42px
}

.centra{
     text-align: center;
}


/* SIDE MENU */
 #side-bar {
     clear: none;
     float: none;
     position: absolute;
     top: 0.5em;
     left: 2em;
     width: 14em;
     padding: 0;
     border: 4px;
     display: table !important;
 }
 #side-bar .side-block {
     padding: 10px;
     border: 1px solid #007eb3;
     border-radius: 9px;
     box-shadow: 0 2px 6px #007eb3;
     background: #fff;
     margin-bottom: 15px;
 }
 #side-bar .side-area {
     padding: 10px;
 }
 #side-bar .heading {
     color: #ffe300;
     border-bottom: solid 1px #007eb3;
     padding-left: 15px;
     margin-top: 10px;
     margin-bottom: 5px;
     font-size: 8pt;
     font-weight: bold;
 }
 #side-bar p {
     margin: 0;
 }
 #side-bar div.menu-item {
     margin: 2px 0;
 }
 #side-bar div.menu-item img {
     width: 13px;
     height: 13px;
     border: 0;
     margin-right: 2px;
     position: relative;
     bottom: -2px;
 }
 #side-bar div.menu-item a {
     font-weight: bold;
}
 #side-bar div.menu-item.inactive img {
     opacity: 0.25;
 }
 #side-bar div.menu-item.inactive a {
     color: #ffe300;
 }
 #side-bar div.menu-item .sub-text {
     font-size: 80%;
     color: #ffe300;
 }
 #side-bar div.menu-item.sub-item {
 }

 #side-bar .collapsible-block-folded {
     background: url('https://files.scpfoundation.net/local--files/scp-1337-ru-j/logo.png') 0 2px no-repeat;
 }
 #side-bar .collapsible-block-link {
     margin-left: 15px;
     font-weight: bold;
 }
 #side-bar .collapsible-block-unfolded-link {
     border-bottom: solid 1px #ffe300;
 }
 #side-bar .collapsible-block-unfolded-link .collapsible-block-link {
     margin-top: 10px;
     margin-bottom: 5px;
     font-size: 8pt;
     color: #ffe300;
 }
 #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
     color: #ffe300;
     text-decoration: none;
 }

 #side-bar ul{
     list-style-type: none;
     padding: 0 5px 0;
 }


/* Search bar */


 #search-top-box-input {
     border: solid 1px #007eb3;
     border-radius: 0px;
     color: #007eb3;
     background-color: #ff00ef;
     box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
 }
 #search-top-box-input:hover,
 #search-top-box-input:focus {
     border: solid 0px #007eb3;
     color: #007eb3;
     background-color: #ff00ef;
     box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
 }
 #search-top-box-form input[type=submit] {
     border: solid 1px #007eb3;
     border-radius: 0px;
     padding: 2px 5px;
     font-size: 90%;
     font-weight: bold;
     color: white;
     background: ff00ef;
     background-color: #007eb3;
     box-shadow: 0 1px 3px rgba(0,0,0,.5);
     cursor: pointer;
 }
 #search-top-box-form input[type=submit]:hover,
 #search-top-box-form input[type=submit]:focus {
     border: solid 1px #007eb3;
     color: #007eb3;
     background: linear-gradient(to bottom, #bbe1fa, #20284e, #2c4a78);
     text-shadow: 0 0 1px rgba(255,255,255,.25);
     background-color: #007eb3;
     box-shadow: 0 1px 3px #007eb3;
 }

div#container-wrap {
     background: url(https://files.scpfoundation.net/local--files/scp-1337-ru-j/ded-moroz_07.gif) top left repeat-x;
     background-size: 100px;
 }

/* Login */

#login-status {
     color: #007eb3;
     font-size: 90%;
     z-index: 30;
 }
 #login-status a {
     background: transparent;
     color: #fff;
 }
 #login-status ul a {
     color: #007eb3;
     background: transparent;
 }

 #account-topbutton {
     background: #ccc;
     color: #007eb3;
 }

 .printuser img.small {
     margin-right: 1px;
 }

/* Header */

#header {
    height: 145px;
    position: left;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    color: #ffe300;
    background-image: url('https://files.scpfoundation.net/local--files/scp-1337-ru-j/logo.png');
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px -70% 25px;
    line-height: 0px;
    max-height: 0px;
    color: #ffe300;
    background: #00ffe7;
    font-family: BauhausLTDemi. 'Nanum Gothic'. Arial. sans-serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "о прикол эту надпись можно менять а что если я вопреки наставлениям сделаю её слишком длинной";
    font-size: .8rem;
    color: #fff;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px #000;
}

@media screen and (max-width: 600px) {
#header h1 a::before {
    content: "о прикол эту надпись можно менять а что если я вопреки наставлениям сделаю её слишком длинной";
    font-size: 1.8rem;
    color: #fff;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px #000;
}
  #header h2 span::before {
    font-size: 21px;
    padding-top: 20px;
  }
}

h1, #page-title {
     font-family: BauhausLTDemi. 'Nanum Gothic'. Arial. sans-serif;
     color: #1050aa;
     padding: 0 0 0.25em;
     margin: 0 0 0.6em;
     font-weight: normal;
     text-align: left;
 }
 h1 {
     margin-top: 0.7em;
     padding: 0;
     font-weight: bold;
 }
 h2, h3, h4, h5, h6, h7 {
